import{S as e,U as t,q as n,t as r,w as i,x as a}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as o}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";r();const s=JSON.parse(`{"title":"赤壁赋","description":"","frontmatter":{},"headers":[{"level":2,"title":"文","slug":"文","link":"#文","children":[]},{"level":2,"title":"名句默写","slug":"名句默写","link":"#名句默写","children":[]}],"relativePath":"gaozhong/01_语文/古诗文/03 赤壁赋.md","filePath":"gaozhong/01_语文/古诗文/03 赤壁赋.md","lastUpdated":1740720525000}`);var c={name:`gaozhong/01_语文/古诗文/03 赤壁赋.md`};function l(r,o,s,c,l,u){let d=n(`RC`);return t(),a(`div`,null,[i(d,{count:`2`,msg:[``]}),o[0]||=e(`<h1 id="赤壁赋" tabindex="-1">赤壁赋 <a class="header-anchor" href="#赤壁赋" aria-label="Permalink to “赤壁赋”">​</a></h1><h2 id="文" tabindex="-1">文 <a class="header-anchor" href="#文" aria-label="Permalink to “文”">​</a></h2><p>壬戌之秋，七月既望，苏子与客泛舟游于赤壁之下。清风徐来，水波不兴。举酒属客，诵明月之诗，歌窈窕之章。少焉，月出于东山之上，徘徊于斗牛之间。白露横江，水光接天。纵一苇之所如，凌万顷之茫然。浩浩乎如冯虚御风，而不知其所止；飘飘乎如遗世独立，羽化而登仙。</p><p>于是饮酒乐甚，扣舷而歌之。歌曰：“桂棹兮兰桨，击空明兮溯流光。渺渺兮予怀，望美人兮天一方。”客有吹洞箫者，倚歌而和之。其声呜呜然，如怨如慕，如泣如诉，余音袅袅，不绝如缕。舞幽壑之潜蛟，泣孤舟之嫠妇。</p><p>苏子愀然，正襟危坐而问客曰：“何为其然也？”客曰：“‘月明星稀，乌鹊南飞’，此非曹孟德之诗乎？西望夏口，东望武昌，山川相缪，郁乎苍苍，此非孟德之困于周郎者乎？方其破荆州，下江陵，顺流而东也，舳舻千里，旌旗蔽空，酾酒临江，横槊赋诗，固一世之雄也，而今安在哉？况吾与子渔樵于江渚之上，侣鱼虾而友麋鹿，驾一叶之扁舟，举匏樽以相属。寄蜉蝣于天地，渺沧海之一粟。哀吾生之须臾，羡长江之无穷。挟飞仙以遨游，抱明月而长终。知不可乎骤得，托遗响于悲风。”</p><p>苏子曰：“客亦知夫水与月乎？逝者如斯，而未尝往也；盈虚者如彼，而卒莫消长也。盖将自其变者而观之，则天地曾不能以一瞬；自其不变者而观之，则物与我皆无尽也，而又何羡乎！且夫天地之间，物各有主，苟非吾之所有，虽一毫而莫取。惟江上之清风，与山间之明月，耳得之而为声，目遇之而成色，取之无禁，用之不竭，是造物者之无尽藏也，而吾与子之所共适。”</p><p>客喜而笑，洗盏更酌。肴核既尽，杯盘狼籍。相与枕藉乎舟中，不知东方之既白。</p><h2 id="名句默写" tabindex="-1">名句默写 <a class="header-anchor" href="#名句默写" aria-label="Permalink to “名句默写”">​</a></h2><p>1．《赤壁赋》中“ ， ”两句，描绘出秋江的爽朗与平静，这也正是诗人悠然自得、怡然自乐的内心写照。</p><details class="details custom-block"><summary>答</summary><p>清风徐来，水波不兴</p></details><p>2．苏轼《赤壁赋》中，写天上的明月从东面山上升起，似乎也像游人一样，深深陶醉在这良辰美景之中，有意把脚步放得很慢的语句是“ ， ”。</p><details class="details custom-block"><summary>答</summary><p>月出于东山之上，徘徊于斗牛之间</p></details><p>3．苏轼在《赤壁赋》中写江上水气弥漫，江水无边无际和远方天际相接的句子是“ ， ”。</p><details class="details custom-block"><summary>答</summary><p>白露横江，水光接天</p></details><p>4．《赤壁赋》中用“ ， ”写任凭一叶扁舟随意漂荡，在辽阔江面上自由来去。</p><details class="details custom-block"><summary>答</summary><p>纵一苇之所如，凌万顷之茫然</p></details><p>5．《赤壁赋》中，写蛟龙听了箫声飞舞起来，寡妇独处孤舟中，听到箫声想到自己身世凄凉，不禁泣下的语句是“ ， ”。</p><details class="details custom-block"><summary>答</summary><p>舞幽壑之潜蛟，泣孤舟之嫠妇。</p></details><p>6．苏轼在《赤壁赋》中概括了曹操军队在攻破荆州顺流而下时的军容盛状的句子是“ ， ”。</p><details class="details custom-block"><summary>答</summary><p>舳舻千里，旌旗蔽空</p></details><p>7．《赤壁赋》中，客人用比喻的修辞手法，感伤人在天地间生命的短暂和个体的渺小的语句是“ ， ”。</p><details class="details custom-block"><summary>答</summary><p>寄蜉蝣于天地，渺沧海之一粟。</p></details><p>8．在《赤壁赋》中，面对浩浩荡荡、奔腾不息的长江，吹洞箫的客人由此发出人生短暂的感叹：“ ， 。”</p><details class="details custom-block"><summary>答</summary><p>哀吾生之须臾，羡长江之无穷。</p></details>`,24)])}var u=o(c,[[`render`,l]]);export{s as __pageData,u as default};