import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"01_如何写好字","description":"","frontmatter":{},"headers":[{"level":3,"title":"规则一：控制好力度","slug":"规则一-控制好力度","link":"#规则一-控制好力度","children":[]},{"level":3,"title":"规则二：控制好大小","slug":"规则二-控制好大小","link":"#规则二-控制好大小","children":[]},{"level":3,"title":"规则三：标点符号","slug":"规则三-标点符号","link":"#规则三-标点符号","children":[]},{"level":3,"title":"规则四：笔","slug":"规则四-笔","link":"#规则四-笔","children":[]}],"relativePath":"gaozhong/01_语文/自己总结/01_如何写好字.md","filePath":"gaozhong/01_语文/自己总结/01_如何写好字.md","lastUpdated":1740720525000}`);var o={name:`gaozhong/01_语文/自己总结/01_如何写好字.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="_01-如何写好字" tabindex="-1">01_如何写好字 <a class="header-anchor" href="#_01-如何写好字" aria-label="Permalink to “01_如何写好字”">​</a></h1><h3 id="规则一-控制好力度" tabindex="-1">规则一：控制好力度 <a class="header-anchor" href="#规则一-控制好力度" aria-label="Permalink to “规则一：控制好力度”">​</a></h3><p>其实如果只是为了让字看起来没那么丑，显得工整，那么做法很简单。就是控制写字的<strong>力度</strong>。</p><p>你可以试一试，如果写字时特别用劲，每一笔、每一画的力度都是很用力，几乎写出来的字不好看。要么看起来太过小学生，要么就是因为用力写字导致写字速度太慢然后乱写显得很乱。</p><p>所以写字要控制好力度。</p><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>也许是控制力度后，就不会有那么多分叉了？</p></div><h3 id="规则二-控制好大小" tabindex="-1">规则二：控制好大小 <a class="header-anchor" href="#规则二-控制好大小" aria-label="Permalink to “规则二：控制好大小”">​</a></h3><p>除此之外，还要控制字的大小。在写考场作文时，一般都会有方框限制住字。此时汉字一般顶格写，且字的大小一致。</p><p>高考中作文的格子线阅卷老师是看不见的，所以字尽量定格写、保持字的大小一致、汉字尽量横平竖直</p><h3 id="规则三-标点符号" tabindex="-1">规则三：标点符号 <a class="header-anchor" href="#规则三-标点符号" aria-label="Permalink to “规则三：标点符号”">​</a></h3><ol><li><p>“，”“。”“？”“！”“？！”“；”逗号、句号、问号、感叹号、问号感叹号统一写到方格的中间下方即可。</p></li><li><p>“————”“... ...”破折号、省略号占满两格。破折号把两个格子当一个格子，写在在两格的中间。省略号每三个占一格，在格子的中间，共占两格。</p></li><li><p>“《》”，““””，“（）”书名号、双引号、括号也是占两格。</p></li><li><p>如果标点符号在首行第一格，那么直接将其写到上一行的末尾。</p></li><li><p>如果遇到数字 2020、1975 这种连着的 4 个数字，那么我们就每两个数字占据一格，数字也是尽量占满格子。</p></li></ol><h3 id="规则四-笔" tabindex="-1">规则四：笔 <a class="header-anchor" href="#规则四-笔" aria-label="Permalink to “规则四：笔”">​</a></h3><p>使用 0.7 直液笔。</p>`,13)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};