import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"01_2_词语替换","description":"","frontmatter":{},"headers":[{"level":2,"title":"不留退路，方能凝志向前","slug":"不留退路-方能凝志向前","link":"#不留退路-方能凝志向前","children":[{"level":3,"title":"运用句子","slug":"运用句子","link":"#运用句子","children":[]}]},{"level":2,"title":"宜滋千里目，且育拏云心","slug":"宜滋千里目-且育拏云心","link":"#宜滋千里目-且育拏云心","children":[{"level":3,"title":"句子","slug":"句子","link":"#句子","children":[]}]},{"level":2,"title":"虽然千万虑吾忘矣","slug":"虽然千万虑吾忘矣","link":"#虽然千万虑吾忘矣","children":[{"level":3,"title":"句子","slug":"句子-1","link":"#句子-1","children":[]}]}],"relativePath":"gaozhong/01_语文/自己总结/0基础学作文作文/01_2_词语替换.md","filePath":"gaozhong/01_语文/自己总结/0基础学作文作文/01_2_词语替换.md","lastUpdated":1740720525000}`);var o={name:`gaozhong/01_语文/自己总结/0基础学作文作文/01_2_词语替换.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="_01-2-词语替换" tabindex="-1">01_2_词语替换 <a class="header-anchor" href="#_01-2-词语替换" aria-label="Permalink to “01_2_词语替换”">​</a></h1><h2 id="不留退路-方能凝志向前" tabindex="-1">不留退路，方能凝志向前 <a class="header-anchor" href="#不留退路-方能凝志向前" aria-label="Permalink to “不留退路，方能凝志向前”">​</a></h2><p>成功：</p><p>奋斗：</p><p>拼搏：</p><p>努力：</p><p>光阴似箭，日月如梭：</p><p>好好学习，努力向上：</p><p>宦官：宦寺</p><p>翅膀：长翮大翼</p><p>奔跑：</p><p>吾辈青年：吾侪青年</p><p>篡权夺位：</p><p>篡改：</p><p>闪耀：闪烁</p><p>世界各国：寰球诸国（寰，宝盖头 + 四 + 哀字去一点）</p><p>对联：楹联（挂、贴在柱子上的对联）</p><p>举报：弹劾（注意弹劾的劾，是力字旁）</p><p>专心：凝志</p><p>项羽：项籍</p><p>打了胜仗：一战功成</p><p>熟悉：熟谙（言字旁）</p><p>开船：御舟（注意不是驾驭的驭）</p><p>给人启发：惹人深思</p><p>平凡无聊：庸常琐碎</p><p>坚定：毅然</p><p>小心谨慎：临深履薄</p><p>创造伟大事业：铸就卓伟之业</p><p>失败：受挫；认输；</p><p>击败：剿灭</p><p>国家危亡：国倾；板荡；朝危；</p><p>想方设法：运筹；布局；运筹帷幄之中；决胜千里之外；（帷幄左边都是巾字旁）</p><p>生计，生活：樵苏</p><p>百姓：黎民；生民；天下苍生；</p><p>战争：兵燹（上面两个逐，下面一个火）</p><p>追求：追逐（追逐的逐，没有一点。一个走字底 + 豕（猪））</p><p>猪：彘（上面一个互字去一横，下面一个比中间插一个箭矢）</p><p>豕（shǐ）：猪。长嘴猪，象形字，</p><p>害怕不前：畏葸不前（葸，害怕，畏惧：畏～不前。）</p><p>泯然于世：寂寂无名（泯然，消失。指原有的特点完全消失了。）</p><p>宦官擅权：宦官篡权（篡，上面是一个算，下面是一个么没有一撇）</p><p>遵守：恪守</p><p>惨遭杀害：惨遭戕害</p><p>荣誉：嘉誉</p><p>闪耀：璀璨（两个王字旁）</p><p>了解：洞悉</p><p>用力投入：倾力投入</p><p>告示：昭示</p><h3 id="运用句子" tabindex="-1">运用句子 <a class="header-anchor" href="#运用句子" aria-label="Permalink to “运用句子”">​</a></h3><ul><li>为官，是为了黎民百姓能乐于樵苏；为官，是为了天下苍生能幸福安康。</li></ul><h2 id="宜滋千里目-且育拏云心" tabindex="-1">宜滋千里目，且育拏云心 <a class="header-anchor" href="#宜滋千里目-且育拏云心" aria-label="Permalink to “宜滋千里目，且育拏云心”">​</a></h2><p>开阔视野：千里目</p><p>崇高指向：拏云心；</p><p>培养：滋养；培育；</p><p>髟：biāo，毛发下垂的样子：“斑鬓～以承弁兮。”（斑鬓髟以承弁兮，素发飒以垂领：两鬓已经斑白还戴着弁帽（古代的一种帽子，通常表示一定的身份或官职）啊，白发纷纷垂到了衣领上。“飒”形容头发纷纷飘落或下垂的样子。弁（biàn）：古代的一种帽子）</p><p>小孩子：垂髫（髟 + 召）；总角。</p><p>颊发：鬓（髟 + 宾）</p><p>邻居：邻里</p><p>赞美：赞誉</p><p>存钱：存钱。</p><p>父母：椿萱（和木和草有关，一个木字旁，一个草字头）</p><p>狭窄：狭隘（xiá ài）（左耳旁 + 益）</p><p>广阔：浩瀚（三点水 + 翰，两个羽）</p><p>理想：星空明月</p><p>世俗追求：六便士</p><p>局限：囿于</p><p>少年应有远大志向：诚如李长吉所言“少年心事当拏云”所言</p><p>远大志向：鸿鹄之志、凌云之志；</p><p>年：春秋；寒暑；星霜。</p><p>二：廿</p><p>三：卅</p><p>劳碌奔波：栉风沐雨（用风梳头，用雨洗脸）</p><p>创业艰辛：筚路蓝缕</p><p>让：教</p><p>谁好谁坏：孰优孰劣</p><p>时光飞逝：流光匆遽（cōngjù，急忙）</p><p>世界广大：天涯无极</p><p>展翅高飞：长翮击天</p><h3 id="句子" tabindex="-1">句子 <a class="header-anchor" href="#句子" aria-label="Permalink to “句子”">​</a></h3><ul><li><p>带上孩子，去大好河川触摸天地花叶，让自然风光开阔胸怀；去文化名城吐纳千古历史，让人文积淀充实思想；去浩瀚书海泛舟文学艺术，让贤哲巨匠唤醒灵魂。</p></li><li><p>要让孩子如南仁东一般，心怀宇宙真知，才能廿二春秋栉风沐雨，令窝凼洼谷醒天眼；如黄大发一般，心怀黎民苍生，才能卅六寒暑筚路蓝缕，换绝巘危崖通涓流；如林俊德一般，心怀国家与名族，才能五十星霜深藏功与名，教瀚海黄沙腾核云。</p></li><li><p>正如普希金所说：一切都是瞬息，一切都将会过去。</p></li><li><p>流光匆遽，少年珍贵，愿我们每一位同学接受的教育，都不沦于狭隘、庸常，而能更多关注开阔、崇高，进而让我们舒展视野更广的眼、跳动志向更高的心，去乘风振奋，拔节生长（主茎的各节长得很快，比喻快速生长）。</p></li><li><p>天涯无极，宜滋千里目;长翮击天，且育孥云心。谢谢。</p></li></ul><h2 id="虽然千万虑吾忘矣" tabindex="-1">虽然千万虑吾忘矣 <a class="header-anchor" href="#虽然千万虑吾忘矣" aria-label="Permalink to “虽然千万虑吾忘矣”">​</a></h2><p>铭记在心：谨记在心</p><p>批判文章：檄文</p><p>看不起：藐视</p><p>内斗：萧墙之斗</p><p>走路摇晃跌撞：蹒跚</p><h3 id="句子-1" tabindex="-1">句子 <a class="header-anchor" href="#句子-1" aria-label="Permalink to “句子”">​</a></h3><ul><li><p>顾虑是鱼中细骨，因为害怕吃鱼卡喉，所以我们拒绝品尝、因废食;顾虑是花下尖刺， 因为害怕拈花割手，所以我们拒绝采拭，远远眺望;顾虑是石上裂痕，因为害怕割石毁宝， 所以我们左顾右盼，退缩推诿。</p></li><li><p>抛却顾虑让一个人挺直脊梁，无畏向前。人生一世，只要想要有所作为，就难免有种种顾虑， 此时只有将之抛开，才能全力奔跑。不由想到了那敢于抛却顾虑的不屈斗士，鲁迅先生。试想 若不能抛却挑战传统的顾虑，他如何能在那个“不知是日是夜”的时代率先向吃人的礼教投 出《狂人日记》的标枪？若不能抛却政府通缉的顾虑，他如何能在刘和珍君的葬礼上写下鼓 舞真的猛士直面淋漓鲜血的檄文？若不能抛却独善其身的顾虑，他如何能在劣根深种的“铁 ”鲁迅这 屋子”中敲响唤醒昏睡的国民的暮鼓晨钟？“寄意寒星荃不察，我以我血荐轩辕。 发出抛却顾虑的呐喊，带领着在野草中彷徨的大众，冷对千夫，横眉向前。 位“戎马书生” ， 他也因此成为了永不会被我们淡忘的“民族魂”。</p></li><li><p>抛却顾虑让一个民族代代传承，自强不息。白云苍狗，金乌轮回，这无限绵延的时光之河 涤荡了几多飞沙，却无法撼动一个抛却顾虑的民族。中华民族，一个在古老的大陆上无数次 蹒跚但从未真正跌倒的民族，天灾之祸、异族之侵、萧墙之斗都无法彻底将她击倒。因为在 这个母体里，抛却顾虑，早已化作一种血统恒久流淌：抛却顾虑，早已化作一种水土滋养四方: 抛却顾虑，早已化作一种精神，强健我们凤翼天翔的脊梁！</p></li></ul>`,88)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};