import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"05_一个通用的作文结构模板，作文至少提高 10 分","description":"","frontmatter":{},"headers":[{"level":2,"title":"结构模板","slug":"结构模板","link":"#结构模板","children":[{"level":3,"title":"1、积累一些 8 字或 10 字的句子","slug":"_1、积累一些-8-字或-10-字的句子","link":"#_1、积累一些-8-字或-10-字的句子","children":[]},{"level":3,"title":"2、根据作文主题，替换句子中的主题词，化用句子","slug":"_2、根据作文主题-替换句子中的主题词-化用句子","link":"#_2、根据作文主题-替换句子中的主题词-化用句子","children":[]}]},{"level":2,"title":"使用小贴士","slug":"使用小贴士","link":"#使用小贴士","children":[]},{"level":2,"title":"结语","slug":"结语","link":"#结语","children":[]}],"relativePath":"gaozhong/01_语文/自己总结/0基础学作文作文/05_一个通用的作文结构模板，作文至少提高10分.md","filePath":"gaozhong/01_语文/自己总结/0基础学作文作文/05_一个通用的作文结构模板，作文至少提高10分.md","lastUpdated":1740720525000}`);var o={name:`gaozhong/01_语文/自己总结/0基础学作文作文/05_一个通用的作文结构模板，作文至少提高10分.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="_05-一个通用的作文结构模板-作文至少提高-10-分" tabindex="-1">05_一个通用的作文结构模板，作文至少提高 10 分 <a class="header-anchor" href="#_05-一个通用的作文结构模板-作文至少提高-10-分" aria-label="Permalink to “05_一个通用的作文结构模板，作文至少提高 10 分”">​</a></h1><h2 id="结构模板" tabindex="-1">结构模板 <a class="header-anchor" href="#结构模板" aria-label="Permalink to “结构模板”">​</a></h2><p>高中作文，其结构主要是这 3 种：并列式、递进式、对比式。</p><p><strong>每次写作，我们都要先确定主题，要有一个“中心论点”，然后一般有两到三个分论点，来为中心论点服务，从不同的角度来论证这个中心论点。</strong></p><p>怎么论证呢？</p><p>给大家介绍一个万能的结构模板，学好这 3 步，任何作文结构都不愁。</p><h3 id="_1、积累一些-8-字或-10-字的句子" tabindex="-1">1、积累一些 8 字或 10 字的句子 <a class="header-anchor" href="#_1、积累一些-8-字或-10-字的句子" aria-label="Permalink to “1、积累一些 8 字或 10 字的句子”">​</a></h3><p><strong>名人名言、歌词、诗句都可以，关键是要对仗工整、结构整饬。</strong></p><p>如“容人之过，修己之身”“浊者自浊，清者自清”“欲得真学问，须下苦功夫”“世上无难事，只要肯登攀”“欲穷千里目，更上一层楼”“一杯敬朝阳，一杯敬月光”“潮涌催人进，风正好扬帆”……</p><h3 id="_2、根据作文主题-替换句子中的主题词-化用句子" tabindex="-1">2、根据作文主题，替换句子中的主题词，化用句子 <a class="header-anchor" href="#_2、根据作文主题-替换句子中的主题词-化用句子" aria-label="Permalink to “2、根据作文主题，替换句子中的主题词，化用句子”">​</a></h3><p><span class="text-red">这步是重点，如何化用？</span></p><p><strong>就是要找准我们的作文主题词，用我们的主题词替换句子中的主题。</strong></p><p>如果我们的作文主题是“青年一代”，作文可以围绕“燃烧青春，报效国家”来写。</p><p>日本作家太宰治在《人间失格》里有句话，“生而为人，我很抱歉”。</p><p>这句话，我们就可以结合“青年一代”的主题，化用为“生而为青年，必须修身”“生而为青年，必须渡青年”“生而为青年，必须担责”3 个分论点。</p><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>修身，可以理解为严格要求自己。</p></div><p>然后我们再从这 3 个方面来论证我们的主题。</p><p>“生而为人，必须修身”，我们就可以讲一些古人修身养性的故事；“生而为人，必须渡人”，我们就可以举一些帮助他人的具体事例，如感动中国十大人物张桂梅的故事；“生而为人，必须担责”我们就可以用一些革命先辈或抗疫英雄，勇挑重担的素材来论证。</p><p><strong>这样，化用“生而为人，我很抱歉”一句话，我们先确定了 3 个分论点，并用 3 个分论点层次清晰地写出了作文的主干，是不是很简单？</strong></p><h2 id="使用小贴士" tabindex="-1">使用小贴士 <a class="header-anchor" href="#使用小贴士" aria-label="Permalink to “使用小贴士”">​</a></h2><p><strong>注意！所选的句子必须是对仗工整的句子，这样用起来才能使作文层次清楚、结构清晰，让人一看高级感满满。</strong></p><h2 id="结语" tabindex="-1">结语 <a class="header-anchor" href="#结语" aria-label="Permalink to “结语”">​</a></h2><p>今天的这个结构模板，大家学会了吗？可能与我前面的文章相比，稍稍有点难度，但只要多用两次，掌握了方法，其实一点儿也不难。</p><p><strong>关键还是要多积累一些名言金句，手里有粮才能心中不慌。</strong></p><p>用好了今天的方法，你的作文至少能提高 10 分，加油吧！</p>`,25)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};