import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"20_99 个简练大气的高级词汇，拯救作文大白话，助力作文上 50","description":"","frontmatter":{},"headers":[{"level":2,"title":"一、2 字词语：","slug":"一、2-字词语","link":"#一、2-字词语","children":[]},{"level":2,"title":"二、4 字词语","slug":"二、4-字词语","link":"#二、4-字词语","children":[]},{"level":2,"title":"三、结语","slug":"三、结语","link":"#三、结语","children":[]}],"relativePath":"gaozhong/01_语文/自己总结/0基础学作文作文/20_99个简练大气的高级词汇，拯救作文大白话，助力作文上50.md","filePath":"gaozhong/01_语文/自己总结/0基础学作文作文/20_99个简练大气的高级词汇，拯救作文大白话，助力作文上50.md","lastUpdated":1740720525000}`);var o={name:`gaozhong/01_语文/自己总结/0基础学作文作文/20_99个简练大气的高级词汇，拯救作文大白话，助力作文上50.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="_20-99-个简练大气的高级词汇-拯救作文大白话-助力作文上-50" tabindex="-1">20_99 个简练大气的高级词汇，拯救作文大白话，助力作文上 50 <a class="header-anchor" href="#_20-99-个简练大气的高级词汇-拯救作文大白话-助力作文上-50" aria-label="Permalink to “20_99 个简练大气的高级词汇，拯救作文大白话，助力作文上 50”">​</a></h1><p>我们在读满分作文或《人民日报》的文章时，总觉读来高端大气、余香满口，不禁赞叹作者的锦绣文笔。让文章看起来格调高、上档次的一个小心机，就是词汇的丰富高级。</p><p><strong>其实，词语本无所谓“低级”或“高级”，但如果你通篇作文都使用大白话，即使你文章立意再高，论证再严密，结构再整饬，也让人感觉难登大雅之堂。</strong></p><p>我们小学写作文时，常用的形容词是“美丽”，美丽的校园、美丽的风景、美丽的心灵、美丽的衣裳……但到了高中，如果我们形容事物还只会用“美丽”，显然是不够的。</p><p>就如电子产品需要更新换代，我们也要给自己的词语库升升级啦！</p><p>这次为大家推荐 99 个格调满满、简练大气的高级词语，助力你的词汇库从 1.0 版升级为 2.0 版，经常使用，一定能为你的作文锦上添花。</p><h2 id="一、2-字词语" tabindex="-1">一、2 字词语： <a class="header-anchor" href="#一、2-字词语" aria-label="Permalink to “一、2 字词语：”">​</a></h2><p>努力→砥砺</p><p>片刻→须臾</p><p>从前→昔日</p><p>波纹→涟漪</p><p>坚持→秉持</p><p>暗淡→幽黯</p><p>缺失→匮乏</p><p>落后→滞后</p><p>关系→关联</p><p>徘徊→踯躅</p><p>缠绵→缱绻</p><p>抬头→昂首</p><p>天边→天际</p><p>随即→旋即</p><p>观看→饱览</p><p>磨练→淬炼</p><p>记载→镌刻</p><p>惋惜→唏嘘</p><p>停止→停滞</p><p>卷入→裹挟</p><p>启示→昭示</p><p>标准→圭臬</p><p>回头→回眸</p><p>安静→静谧</p><p>匆忙→倥偬</p><p>遵守→恪守</p><p>呆板→木讷</p><p>吵闹→聒噪</p><p>奇怪→吊诡</p><p>差距→鸿沟</p><p>放弃→摒弃</p><p>选拔→遴选</p><p>继承→秉承</p><p>熟知→谙悉</p><p>狭窄→逼仄</p><p>很快→倏忽</p><p>措施→举措</p><p>勇敢→勇毅</p><p>吸取→汲取</p><p>等待→亟待</p><p>着急→焦灼</p><p>消失→泯灭</p><p>清除→涤荡</p><p>加持→赋能</p><p>熟悉→熟稔</p><p>插嘴→置喙</p><p>束缚→桎梏</p><p>拜访→拜谒</p><p>虚度→蹉跎</p><p>质问→诘问</p><h2 id="二、4-字词语" tabindex="-1">二、4 字词语 <a class="header-anchor" href="#二、4-字词语" aria-label="Permalink to “二、4 字词语”">​</a></h2><p>起早贪黑→夙兴夜寐</p><p>不顾风雨→栉风沐雨</p><p>指出问题→针砭时弊</p><p>支离破碎→分崩离析</p><p>到处都是→俯拾皆是</p><p>随手拿来→信手拈来</p><p>欣喜若狂→额手称庆</p><p>众望所归→深孚众望</p><p>继续下去→薪火绵延</p><p>事到如今→迄今为止</p><p>信守承诺→守信然诺</p><p>克服困难→共克时艰</p><p>归根结底→静言思之</p><p>发人深省→掷地有声</p><p>艰苦奋斗→筚路蓝缕</p><p>下定决心→矢志不移</p><p>不忘初心→初心如磐</p><p>无需怀疑→毋庸置疑</p><p>相互配合→交相辉映</p><p>高雅艺术→黄钟大吕</p><p>通俗艺术→山歌村笛</p><p>彻底醒悟→醍醐灌顶</p><p>臭味相投→沆瀣一气</p><p>表现突出→大放异彩</p><p>发光发亮→熠熠生辉</p><p>相差极大→云泥之别</p><p>同心协力→和衷共济</p><p>荒凉破败→断井颓垣</p><p>情势紧迫→迫在眉睫</p><p>胸怀坦荡→光风霁月</p><p>力量单薄→独木难支</p><p>永不止步→驰而不息</p><p>不露锋芒→和光同尘</p><p>一直以来→一以贯之</p><p>心情失落→怅然若失</p><p>志趣相投→惺惺相惜</p><p>轻而易举→一蹴而就</p><p>终有回报→功不唐捐</p><p>妄作评论→信口雌黄</p><p>反省过错→责躬省过</p><p>奋勇前进→百舸争流</p><p>横行霸道→暴戾恣睢</p><p>一目了然→炳如观火</p><p>坚定不移→历久弥坚</p><p>积攒实力→蓄势待发</p><p>千秋万代→万古长空</p><p>得寸进尺→肆无忌惮</p><p>精神振奋，斗志昂扬→踔厉奋发</p><p>履行所学，永不倦怠→笃行不怠</p><h2 id="三、结语" tabindex="-1">三、结语 <a class="header-anchor" href="#三、结语" aria-label="Permalink to “三、结语”">​</a></h2><p>用词的准确与贴切是每个写作人必备的基本功。意思大致相同的词汇，也有极细微的差别。就如“落后”和“滞后”两个词，滞后一般指行动落后，而落后多指一个人的成长进步落后于别人。</p><p>再如“踔厉奋发”与“笃行不怠”这两个词是《人民日报》常用的高频词，常用来形容奋发图强的坚定决心，号召大家付诸行动等，希望同学们在使用这些高级词时，也要关注词语的语言环境及细微差别。</p><p>希望今天的分享对你有用。</p>`,111)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};