import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"011 度","description":"","frontmatter":{},"headers":[{"level":2,"title":"度，dù","slug":"度-du","link":"#度-du","children":[]},{"level":2,"title":"度，duó","slug":"度-duo","link":"#度-duo","children":[]},{"level":2,"title":"口诀","slug":"口诀","link":"#口诀","children":[]},{"level":2,"title":"小测试","slug":"小测试","link":"#小测试","children":[]}],"relativePath":"gaozhong/01_语文/自己总结/144 字搞定文言文/011 度.md","filePath":"gaozhong/01_语文/自己总结/144 字搞定文言文/011 度.md","lastUpdated":1758138895000}`);var o={name:`gaozhong/01_语文/自己总结/144 字搞定文言文/011 度.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="_011-度" tabindex="-1">011 度 <a class="header-anchor" href="#_011-度" aria-label="Permalink to “011 度”">​</a></h1><p>du，duo。多音字。当读音不同时，意思也不一样。</p><img class="w-40%" src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/public/assets/gaozhong_huaxue/Snipaste_2024-08-26_01-04-48.png"><p>下面是一个手，代表用手进行测量。</p><h2 id="度-du" tabindex="-1">度，dù <a class="header-anchor" href="#度-du" aria-label="Permalink to “度，dù”">​</a></h2><p>① 计量长短的标准，例：秦朝统一了度量衡。</p><p>② 法制、制度。</p><p>③ 气度，胸怀。</p><p>④ 渡过。</p><p>《木兰诗》：“万里赴戎机，关山度若飞。”</p><p>不远万里奔赴战场，渡过关山像飞一样。</p><div class="tip custom-block"><p class="custom-block-title">度 和 渡的使用</p><ul><li>渡 -&gt; 用于空间</li><li>度 -&gt; 用于时间</li></ul></div><p>⑤ 量词，次。</p><p>《江南逢李龟年》：“岐王宅里寻常见，崔九堂前几度闻。”</p><h2 id="度-duo" tabindex="-1">度，duó <a class="header-anchor" href="#度-duo" aria-label="Permalink to “度，duó”">​</a></h2><p>① 动词，测量，引申为揣测、估计。</p><h2 id="口诀" tabindex="-1">口诀 <a class="header-anchor" href="#口诀" aria-label="Permalink to “口诀”">​</a></h2><p>测量出标准形成法度。</p><h2 id="小测试" tabindex="-1">小测试 <a class="header-anchor" href="#小测试" aria-label="Permalink to “小测试”">​</a></h2><p>你能说说下面“度”字的解释吗？</p><ol><li>《凉州词》：“春风不度玉门关。”</li></ol><p>春风渡不过玉门关。</p><ol start="2"><li>《郑人买履》：“已得履，乃曰：‘吾忘持度。’”</li></ol><p>这句话的翻译是：“已经拿到了鞋子，却（回头）说：‘我忘记带尺码了。’”</p><p>这句话出自《韩非子·外储说左上》中的《郑人买履》故事。原文讲述的是郑国有个想买鞋子的人，他先在家里量了自己的脚，把量好的尺码放在了自己的座位上。到了集市，却忘了带上尺码。挑好了鞋子，才发现：“我忘了带尺码。”就返回家中拿尺码。等到他返回集市的时候，集市已经散了，他最终没有买到鞋子。有人问：“你为什么不直接用自己的脚去试鞋子？”他回答说：“我宁可相信量好的尺码，也不相信自己的脚。”这个故事讽刺了那些墨守成规、不尊重客观事实的人。</p>`,25)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};