import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"其它","slug":"其它","link":"#其它","children":[{"level":3,"title":"执抟挫锐，噬食鬼魅","slug":"执抟挫锐-噬食鬼魅","link":"#执抟挫锐-噬食鬼魅","children":[]}]}],"relativePath":"gaozhong/01_语文/自己总结/字词/其它.md","filePath":"gaozhong/01_语文/自己总结/字词/其它.md","lastUpdated":1740720525000}`);var o={name:`gaozhong/01_语文/自己总结/字词/其它.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h2 id="其它" tabindex="-1">其它 <a class="header-anchor" href="#其它" aria-label="Permalink to “其它”">​</a></h2><h3 id="执抟挫锐-噬食鬼魅" tabindex="-1">执抟挫锐，噬食鬼魅 <a class="header-anchor" href="#执抟挫锐-噬食鬼魅" aria-label="Permalink to “执抟挫锐，噬食鬼魅”">​</a></h3><p>zhí tuán cuò ruì</p><p>“虎者，阳物，百兽之长也，能执搏挫锐，噬食鬼魅。”东汉《风俗通义•祀典》。</p><p>“执抟挫锐，噬食鬼魅”这句话通常被认为是描述中国古代神话中的白虎神兽的特性和能力。但请注意，原句应为“执搏挫锐，噬食鬼魅”。以下是逐字翻译和整体解释：</p><ol><li>执搏： <ul><li>“执”：执掌、掌握。</li><li>“搏”：搏斗、搏杀。所以，“执搏”意为执掌或掌握搏杀，表示白虎有掌握争斗、战斗的能力。</li></ul></li><li>挫锐： <ul><li>“挫”：挫败、制服。</li><li>“锐”：锐利、锐气，通常指兵器的锋利或军队的锐气。因此，“挫锐”意为挫败敌人的锐气或力量。</li></ul></li><li>噬食： <ul><li>“噬”：咬。</li><li>“食”：吃。所以，“噬食”表示咬食、吞食。</li></ul></li><li>鬼魅： <ul><li>在中国古代神话中，鬼魅通常指邪恶、不祥的妖怪或幽灵。</li></ul></li></ol><p>整体解释：</p><p>“执搏挫锐，噬食鬼魅”描述的是白虎神兽具有执掌争斗、挫败敌人锐气，并能吞噬邪恶鬼魅的能力。在中国传统文化中，白虎被视为西方神兽，具有守护和驱邪的功能，因此常被画成画像贴在门上，以求家宅平安、驱邪避凶。</p>`,8)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};