import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"县试、府试、乡试、会试、殿试，古代读书人的科举之路要一步步来","description":"","frontmatter":{},"headers":[{"level":2,"title":"考试之路","slug":"考试之路","link":"#考试之路","children":[{"level":3,"title":"第一场考试：县试","slug":"第一场考试-县试","link":"#第一场考试-县试","children":[]},{"level":3,"title":"第二场考试：府试","slug":"第二场考试-府试","link":"#第二场考试-府试","children":[]},{"level":3,"title":"第三场考试：乡试","slug":"第三场考试-乡试","link":"#第三场考试-乡试","children":[]},{"level":3,"title":"第四场考试：会试","slug":"第四场考试-会试","link":"#第四场考试-会试","children":[]},{"level":3,"title":"第五场考试：殿试","slug":"第五场考试-殿试","link":"#第五场考试-殿试","children":[]}]},{"level":2,"title":"当官","slug":"当官","link":"#当官","children":[{"level":3,"title":"进翰林院","slug":"进翰林院","link":"#进翰林院","children":[]},{"level":3,"title":"庶吉士","slug":"庶吉士","link":"#庶吉士","children":[]},{"level":3,"title":"授官","slug":"授官","link":"#授官","children":[]}]}],"relativePath":"gaozhong/01_语文/自己总结/文章/县试、府试、乡试、会试、殿试，古代读书人的科举之路要一步步来.md","filePath":"gaozhong/01_语文/自己总结/文章/县试、府试、乡试、会试、殿试，古代读书人的科举之路要一步步来.md","lastUpdated":1740720525000}`);var o={name:`gaozhong/01_语文/自己总结/文章/县试、府试、乡试、会试、殿试，古代读书人的科举之路要一步步来.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="县试、府试、乡试、会试、殿试-古代读书人的科举之路要一步步来" tabindex="-1">县试、府试、乡试、会试、殿试，古代读书人的科举之路要一步步来 <a class="header-anchor" href="#县试、府试、乡试、会试、殿试-古代读书人的科举之路要一步步来" aria-label="Permalink to “县试、府试、乡试、会试、殿试，古代读书人的科举之路要一步步来”">​</a></h1><p>科举制度是历代封建王朝通过考试选拔官吏的一种制度，是中国最早的“高考”。</p><p>采用分科选举优秀者的办法，所以叫科举。从隋代至明清，科举制实行了一千三百多年。状元，这一至今仍充满魅力和活力并为众人羡慕的名词，则是中国历史上唯一一位女皇帝武则天的发明和创造。</p><p>首先，我们来看一看古代读书人考试之路的漫长与艰难。</p><h2 id="考试之路" tabindex="-1">考试之路 <a class="header-anchor" href="#考试之路" aria-label="Permalink to “考试之路”">​</a></h2><h3 id="第一场考试-县试" tabindex="-1">第一场考试：县试 <a class="header-anchor" href="#第一场考试-县试" aria-label="Permalink to “第一场考试：县试”">​</a></h3><p>由考生所在县的县官主持，考期多在每年的阴历二月。通过县试的考生获得参加府试的资格，统称为童生。</p><h3 id="第二场考试-府试" tabindex="-1">第二场考试：府试 <a class="header-anchor" href="#第二场考试-府试" aria-label="Permalink to “第二场考试：府试”">​</a></h3><p>由知府主持，考期多在每年的阴历四月。通过府试的考生获得参加院试的资格，统称为生员或秀才。</p><h3 id="第三场考试-乡试" tabindex="-1">第三场考试：乡试 <a class="header-anchor" href="#第三场考试-乡试" aria-label="Permalink to “第三场考试：乡试”">​</a></h3><p>每三年在各省省城（包括京城）举行的一次考试，因在秋季阴历八月初九、十二、十五三天举行，故又称秋闱。主考官由皇帝委派。考后发布正、副榜，正榜所取的人叫举人，第一名叫解元。</p><h3 id="第四场考试-会试" tabindex="-1">第四场考试：会试 <a class="header-anchor" href="#第四场考试-会试" aria-label="Permalink to “第四场考试：会试”">​</a></h3><p>每三年在京城举行的一次考试，因在春季阴历二月初九、十二、十五三天举行，故又称春闱。考试由礼部主持，皇帝任命正、副总裁，各省的举人及国子监监生皆可应考，录取三百名为贡士，第一名叫会元。</p><h3 id="第五场考试-殿试" tabindex="-1">第五场考试：殿试 <a class="header-anchor" href="#第五场考试-殿试" aria-label="Permalink to “第五场考试：殿试”">​</a></h3><p>殿试是科举制最高级别的考试，皇帝在殿廷上，对会试录取的贡士亲自策问，以定甲第。</p><p>实际上皇帝有时委派大臣主管殿试，并不亲自策问。</p><p>录取分为三甲：</p><p>一甲三名，赐&quot;进士及第&quot;的称号，第一名称状元，第二名称榜眼，第三名称探花；</p><p>二甲若干名，赐&quot;进士出身&quot;的称号；</p><p>三甲若干名，赐&quot;同进士出身&quot;的称号。</p><p>二、三甲第一名皆称传胪，一、二、三甲统称进士。</p><h2 id="当官" tabindex="-1">当官 <a class="header-anchor" href="#当官" aria-label="Permalink to “当官”">​</a></h2><h3 id="进翰林院" tabindex="-1">进翰林院 <a class="header-anchor" href="#进翰林院" aria-label="Permalink to “进翰林院”">​</a></h3><p>状元，进翰林院，官职从六品授修撰，比知县高一级。</p><p>榜眼、探花进翰林院，官职授正七品编修。</p><p>虽然品级不高，但状元、榜眼、探花有选任为某些职官及进入中央高层的资格，所以出路比较优越。</p><h3 id="庶吉士" tabindex="-1">庶吉士 <a class="header-anchor" href="#庶吉士" aria-label="Permalink to “庶吉士”">​</a></h3><p>其他进士们再进行一次朝考，主要为了选拔庶吉士。朝考中选拨出优秀的进士为庶吉士，入庶常馆学习，三年期满时考试，分配工作，叫散馆，若有恩科，不满三年也叫散馆。</p><h3 id="授官" tabindex="-1">授官 <a class="header-anchor" href="#授官" aria-label="Permalink to “授官”">​</a></h3><p>没有录取为庶吉士的进士授官，其品级主要是六、七品。</p>`,30)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};