import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"02 海瑞","description":"","frontmatter":{},"headers":[],"relativePath":"gaozhong/01_语文/自己总结/素材/02海瑞.md","filePath":"gaozhong/01_语文/自己总结/素材/02海瑞.md","lastUpdated":1740720525000}`);var o={name:`gaozhong/01_语文/自己总结/素材/02海瑞.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="_02-海瑞" tabindex="-1">02 海瑞 <a class="header-anchor" href="#_02-海瑞" aria-label="Permalink to “02 海瑞”">​</a></h1><p>明世宗朱厚熜晚年，不在朝堂处理政务，深居西苑，专心设坛求福。</p><p>嘉靖四十五年（1566 年）二月一日，海瑞在棺材铺里<span class="text-red">买好了棺材</span>，并且将自己的家人托付给了一个朋友。然后向明世宗呈上《治安疏》，批评世宗迷信巫术，生活奢华，不理朝政等弊端。</p><p>明世宗读了海瑞的《治安疏》，十分愤怒，把《治安疏》扔在地上，对左右侍从说：“快把他逮起来，不要让他跑掉。”宦官黄锦在旁边说：“这个人向来有愚名。听说他上疏之前，自己知道冒犯该死，买了一个棺材，和妻子诀别，奴仆们也四处奔散没有留下来的，他自己是不会逃跑的。”明世宗听了默默无言。过了一会又读海瑞的上疏，一天里反复读了多次，感到叹息，只得把《治安疏》留在宫中数月。曾说：“这个人可与比干相比，但朕不是商纣王。”</p><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>驱散家人抬棺上奏，海瑞不留退路，凝志向前，终感动帝王。</p></div>`,5)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};