import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"05 康熙皇帝","description":"","frontmatter":{},"headers":[{"level":2,"title":"成就","slug":"成就","link":"#成就","children":[{"level":3,"title":"平定三藩之乱","slug":"平定三藩之乱","link":"#平定三藩之乱","children":[]},{"level":3,"title":"打赢雅克萨之战","slug":"打赢雅克萨之战","link":"#打赢雅克萨之战","children":[]},{"level":3,"title":"三征噶尔丹","slug":"三征噶尔丹","link":"#三征噶尔丹","children":[]},{"level":3,"title":"收复台湾","slug":"收复台湾","link":"#收复台湾","children":[]}]}],"relativePath":"gaozhong/01_语文/自己总结/素材/05康熙皇帝.md","filePath":"gaozhong/01_语文/自己总结/素材/05康熙皇帝.md","lastUpdated":1758138895000}`);var o={name:`gaozhong/01_语文/自己总结/素材/05康熙皇帝.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="_05-康熙皇帝" tabindex="-1">05 康熙皇帝 <a class="header-anchor" href="#_05-康熙皇帝" aria-label="Permalink to “05 康熙皇帝”">​</a></h1><p>康熙，是清朝第四位皇帝爱新觉罗·玄烨的年号，康，安宁；熙，兴盛，蕴万民康宁，天下熙盛之意，蒙古语为“平和宁静”。</p><p>使用时间为康熙元年（1662 年）至康熙六十一年（1722 年），一共使用六十一年，是清朝使用最长的年号。康熙帝是在中国历史上在位时间最长的皇帝。</p><p>清圣祖仁皇帝爱新觉罗·玄烨（1654 年 5 月 4 日—1722 年 12 月 20 日）即康熙帝，清朝第四位皇帝、清定都北京后第二位皇帝，年号康熙。</p><p>玄烨的皇祖母孝庄太皇太后，十分关心他的成长，对玄烨的培养倾注了大量心血，经常给玄烨讲述祖父清太宗皇太极当年创业大清国的故事，激励他将来要秉承祖先英烈之风，成为有抱负、有作为的人物。玄烨是孝庄皇太后辅佐的第二位皇帝，她一直以帝王的标准严格训练康熙，教育他要“宽裕慈仁，温良恭敬”，甚至连一举一动都要“俨然端坐”，中规中矩。</p><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>康熙在 14 岁时便许下“惟愿天下义安，生民乐业”之宏愿，为将之兑现，他不留退路，凝志向前————败则国倾，他凝志布局，削平三藩；输则板荡，他凝志用人，收复台湾；挫则朝危，他凝志运筹，剿灭葛尔丹。最终，生民乐于樵苏，免受兵燹；盛世永留人间，太平美满。正是因为这位清圣祖一生不留退路，凝志向前，才能于在位的 61 年中绘制了那“万民康宁、天下熙盛”的盛世画卷，也使得自己成为“千古一帝”，站在了历史的风口浪尖紧握住日月旋转。</p></div><h2 id="成就" tabindex="-1">成就 <a class="header-anchor" href="#成就" aria-label="Permalink to “成就”">​</a></h2><p>康熙帝在位时期都有哪些成就，为何被评为千古一帝？</p><p>康熙帝名爱新觉罗·玄烨，是清朝入关后的第二位皇帝，也是中国历史上在位时间最长的皇帝，长达六十一年，康熙也是唯一一个驾崩后被上庙号为“祖”的非开国皇帝（明成祖朱棣的庙号原为太宗；清世祖福临是清朝入关后的第一个皇帝，称祖不过分）。康熙帝在位期间任用贤能，勤政爱民，为国担忧，开疆拓土。正是因为康熙帝做出的成就颇多，所以人们对他的评价极高，后世更是将他评为“千古一帝”。那么康熙帝是凭借哪些成就才被评为千古一帝的呢？</p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-14-12-55-54.png" alt="picgo-2025-02-14-12-55-54"><h3 id="平定三藩之乱" tabindex="-1">平定三藩之乱 <a class="header-anchor" href="#平定三藩之乱" aria-label="Permalink to “平定三藩之乱”">​</a></h3><p>第一，平定三藩之乱。清朝入关后为了消灭李自成和南明，封云南的吴三桂为平西王、广东的尚可喜为平南王、福建的耿精忠为靖南王。康熙十二年康熙帝宣布撤藩，吴三桂便于这年十一月反清，提出“兴明讨虏”的口号，后来吴三桂率军由云贵两省进攻湖南，占领湖南后进而进攻四川，四川的官员纷纷投降。随后福建、广东、广西、陕西、湖北、河南还有台湾等地的众多汉人纷纷响应，清廷形势不容乐观，康熙帝此时急得像热锅上的蚂蚁。但是吴三桂不思进取，只是在湖南沿江布防，不再向北发展，康熙帝抓住机会分化吴三桂的力量，坚决打击吴三桂，再对其余反叛势力进行诏安，在耿精忠、尚之信归顺清廷后，吴三桂已经独木难支。康熙十七年吴三桂于衡州匆匆称帝，不久后病逝。康熙二十年清军攻入昆明，吴世璠自杀，历时八年的三藩之乱被平定。</p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-14-12-57-18.png" alt="picgo-2025-02-14-12-57-18"><h3 id="打赢雅克萨之战" tabindex="-1">打赢雅克萨之战 <a class="header-anchor" href="#打赢雅克萨之战" aria-label="Permalink to “打赢雅克萨之战”">​</a></h3><p>第二，打赢雅克萨之战。沙俄在清军入关后就一直骚扰清朝的边境，还妄图侵占中国黑龙江流域的大片领土。在康熙二十年，清朝向沙俄发出警告，让他们撤离清朝领土。但是沙俄根本不理会，最后清朝被迫反击，对俄军进行了两次围歼战。最终清朝成功驱逐沙俄，收复了领土雅萨克。康熙二十八年中俄签订了中国历史上对外的第一个平等条约《尼布楚条约》，划定了中俄在东北的边境，意义巨大。</p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-14-12-57-41.png" alt="picgo-2025-02-14-12-57-41"><h3 id="三征噶尔丹" tabindex="-1">三征噶尔丹 <a class="header-anchor" href="#三征噶尔丹" aria-label="Permalink to “三征噶尔丹”">​</a></h3><p>第三，三征噶尔丹。噶尔丹为割据西北、统治蒙古诸部，在俄国政府的怂恿支持下，兴兵进攻喀尔喀蒙古土谢图汗部。又借口追击土谢图汗部余众，进军内蒙古乌朱穆沁地区，与清王朝发生直接军事冲突。康熙帝为确保边疆安定，三次亲征漠北。康熙二十九年乌兰布通之战中清军大破噶尔丹的防御营地驼城，一征胜利；康熙三十五年昭莫多之战中清军击溃噶尔丹的主力军，噶尔丹只带了几十名骑兵脱逃了，二征胜利；康熙三十六年噶尔丹拒不投降，康熙帝再次亲征，噶尔丹众叛亲离，走投无路后自杀了，三征胜利。康熙帝三征噶尔丹胜利后，喀尔喀蒙古重新统一于清朝。同时也为后来乾隆帝平定准噶尔打下基础。</p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-14-12-57-57.png" alt="picgo-2025-02-14-12-57-57"><h3 id="收复台湾" tabindex="-1">收复台湾 <a class="header-anchor" href="#收复台湾" aria-label="Permalink to “收复台湾”">​</a></h3><p>第四，收复台湾。康熙年间台湾处于明郑时期。在郑成功死后，他的儿子郑经接手台湾，郑经不想归顺清朝，于是在台湾建立了东宁国，意欲与清廷分庭抗争。康熙二十年郑经去世，年仅 12 岁的郑克塽继承王位，但是大权都被冯锡范、刘国轩掌握。康熙二十二年康熙帝任命施琅为福建水师提督，出兵攻台，施琅不愧为清初名将，在澎湖大败郑氏海军后就收复了台湾。康熙二十三年清朝设立台湾府，加强了中央对台湾的控制和管理。</p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-14-12-58-12.png" alt="picgo-2025-02-14-12-58-12"><p>凭此四件事，康熙帝不仅巩固了清朝在中原的统治地位，还开疆拓土，维护国家统一，所作所为完全称得上是千古一帝。当然康熙帝也是犯过错误的，比如大兴文字狱，实行闭关锁国等等，但是这些也掩盖不了康熙帝的成就。</p>`,23)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};