import{C as e,U as t,t as n,v as r,x as i}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as a}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const o=JSON.parse(`{"title":"07 李贺","description":"","frontmatter":{},"headers":[],"relativePath":"gaozhong/01_语文/自己总结/素材/07李贺.md","filePath":"gaozhong/01_语文/自己总结/素材/07李贺.md","lastUpdated":1740720525000}`);var s={name:`gaozhong/01_语文/自己总结/素材/07李贺.md`};function c(n,a,o,s,c,l){return t(),i(`div`,null,a[0]||=[r(`h1`,{id:`_07-李贺`,tabindex:`-1`},[e(`07 李贺 `),r(`a`,{class:`header-anchor`,href:`#_07-李贺`,"aria-label":`Permalink to “07 李贺”`},`​`)],-1),r(`p`,null,`李贺（790 年—816 年），字长吉，河南府福昌县昌谷乡（今属河南省宜阳县）人，祖籍陇西郡，世称“李昌谷”，唐朝中期浪漫主义诗人。唐朝宗室郑王李亮后裔。`,-1),r(`p`,null,`元和六年（811 年），李贺以父荫得官，任奉礼郎。元和八年（813 年），告病回昌谷休养。元和九年（814 年），辞去奉礼郎之职，重回昌谷“归卧”。后在潞州张彻的荐举下，做了昭义军节度使郗士美的幕僚，帮办公文。27 岁（一说 24 岁）英年早逝。`,-1),r(`p`,null,`李贺是中唐到晚唐诗风转变期的代表者，与李白、李商隐并称为“唐代三李”。他所写的诗大多是感叹生不逢时和倾诉内心苦闷，抒发对理想、抱负的追求；对当时藩镇割据、宦官专权和人民所受的残酷剥削也都有所反映。他的诗作想象极为丰富，经常应用神话传说来托古寓今，所以后人常称他为“鬼才”、“诗鬼”，称其创作的诗文为“鬼仙之辞”。有“太白仙才，长吉鬼才”之说。李贺是继屈原、李白之后，中国文学史上又一位颂享盛誉的浪漫主义诗人。留下了“黑云压城城欲摧”、“雄鸡一声天下白”、“天若有情天亦老”等千古佳句。`,-1),r(`p`,null,`李贺自幼体形细瘦，通眉长爪，长相极有特征。他才思聪颖，七岁能诗，又擅长“疾书”。相传贞元十二年（796 年），李贺正值七岁，韩愈、皇甫湜造访，李贺就能提笔写诗，韩愈与皇甫湜大吃一惊，李贺从此名扬京洛。年纪稍长，李贺白日骑驴觅句，暮则探囊整理，焚膏继晷，十分刻苦。李商隐作《小传》云：“恒从小奚奴，骑巨驴，背一古锦囊，遇有所得，即书投囊中，及暮归，太夫人使婢受囊出之，所见书多，辄曰：‘是儿要当呕出心乃已耳！’”`,-1),r(`p`,null,`元和二年（807 年），李贺已诗名远播，元和三、四年间（808—809 年），李贺写《雁门太守行》谒韩愈，本可早登科第，振其家声，但“年未弱冠”，即遭父丧。当时，服丧务必以三年全期为限，所以直到元和五年（810 年）韩愈才与李贺书，劝其举进士。该年初冬，李贺参加房式主持，韩愈参与组织的河南府试，作《河南府试十二月乐词并闰月》，并一举获隽，年底即赴长安应进士举。可是妒才者放出流言，谓李贺父名“晋肃”，“晋”与“进”犯“嫌名”。尽管韩愈“质之于律”“稽之于典”为其辩解，终无可奈何，李贺不得不愤离试院。`,-1),r(`p`,null,`未能参加进士考试，对李贺打击甚重，他曾写了不少抒愤之诗，当年回到昌谷。元和六年（811 年）五月，李贺又返回长安，经宗人推荐，考核后，父荫得官，任奉礼郎，从九品。从此，“牢落长安”长达 3 年，为官 3 年间，李贺亲身经历，耳闻目睹了许多事情，结交了一批志同道合的朋友，对当时社会状况有了深刻的认识。个人生活虽不如意，却创作了一系列反映现实、鞭挞黑暗的诗篇。所谓贺诗“深刺当世之弊，切中当世之隐”（清姚文燮语），大多数作品就产生在这一时期。`,-1)])}var l=a(s,[[`render`,c]]);export{o as __pageData,l as default};