import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"09 黄大发","description":"","frontmatter":{},"headers":[],"relativePath":"gaozhong/01_语文/自己总结/素材/09黄大发.md","filePath":"gaozhong/01_语文/自己总结/素材/09黄大发.md","lastUpdated":1758138895000}`);var o={name:`gaozhong/01_语文/自己总结/素材/09黄大发.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="_09-黄大发" tabindex="-1">09 黄大发 <a class="header-anchor" href="#_09-黄大发" aria-label="Permalink to “09 黄大发”">​</a></h1><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>“七一勋章”获得者：“当代愚公”黄大发。</p></div><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-16-11-11-54.png" alt="picgo-2025-02-16-11-11-54"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-16-11-12-04.png" alt="picgo-2025-02-16-11-12-04"><p>图为 2016 年 6 月 26 日，黄大发巡查水渠。</p><p><strong>黄大发，一心为民、埋头苦干、百折不挠的楷模。带领村民<span class="text-red">历时 36 年</span>，在悬崖绝壁上开凿出一条主渠长 7200 米、支渠长 2200 米的“生命渠”，用实干兑现“水过不去、拿命来铺”的誓言，为改善山区群众用水条件、实现脱贫致富作出突出贡献，被誉为“当代愚公”。</strong></p><p>黄大发，1935 年 11 月生，1959 年 11 月入党，贵州遵义人，贵州省遵义市播州区平正仡佬族乡原草王坝村党支部书记。1958 年，黄大发被推选为草王坝大队大队长。他立志要为草王坝引来水、修通路、接通电。20 世纪 60 年代起，在他的带领下，村民们攀岩走壁，用钢钎撬、用铁锤砸，但由于技术落后，耗时 10 多年引水工程也没修成。黄大发不肯服输。1989 年，年过半百的他到附近水利站，一边帮工一边学习，只有小学文化的他硬是掌握了许多水利知识。1992 年春，黄大发带领 200 多名村民奔赴工地，引水工程再次动工。1995 年，一条总长 9400 米的水渠绕三重大山、过三道绝壁、穿三道悬崖全线贯通，草王坝彻底告别了“滴水贵如油”的历史。这条水渠被村民们亲切地称为“大发渠”。通水后，黄大发马不停蹄实施“坡改梯”。村里的稻田种植面积从 240 亩增加到 720 亩，老乡们从此不再为吃饭发愁。随后他又带领村民修通 4 公里的通村公路，让村里通了电、建起了学校。</p><p>“共产党员就是要干一辈子，我还要继续奋斗，带富家乡的乡亲。”2004 年退休后，黄大发继续为脱贫致富和乡村振兴发挥余热。黄大发荣获“全国劳动模范”、“时代楷模”、“全国道德模范”等称号。在庆祝中国共产党成立 100 周年“七一勋章”颁授仪式上，习近平总书记亲自向他颁授勋章。</p>`,8)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};