import{S as e,U as t,q as n,t as r,w as i,x as a}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as o}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";r();const s=JSON.parse(`{"title":"29-30 病句","description":"","frontmatter":{},"headers":[{"level":2,"title":"语序不当","slug":"语序不当","link":"#语序不当","children":[{"level":3,"title":"1 关联词语序不当","slug":"_1-关联词语序不当","link":"#_1-关联词语序不当","children":[]},{"level":3,"title":"2 多项定语、状语语序不当","slug":"_2-多项定语、状语语序不当","link":"#_2-多项定语、状语语序不当","children":[]},{"level":3,"title":"3 语义不明","slug":"_3-语义不明","link":"#_3-语义不明","children":[]}]},{"level":2,"title":"歌","slug":"歌","link":"#歌","children":[]}],"relativePath":"gaozhong/01_语文/集合/02 秋季/29-30 病句.md","filePath":"gaozhong/01_语文/集合/02 秋季/29-30 病句.md","lastUpdated":1758138895000}`);var c={name:`gaozhong/01_语文/集合/02 秋季/29-30 病句.md`};function l(r,o,s,c,l,u){let d=n(`RC`);return t(),a(`div`,null,[i(d,{count:`1`,msg:[``]}),o[0]||=e(`<h1 id="_29-30-病句" tabindex="-1">29-30 病句 <a class="header-anchor" href="#_29-30-病句" aria-label="Permalink to “29-30 病句”">​</a></h1><p><a href="/gaozhong/语文/字词句本.html">病句</a></p><h2 id="语序不当" tabindex="-1">语序不当 <a class="header-anchor" href="#语序不当" aria-label="Permalink to “语序不当”">​</a></h2><h3 id="_1-关联词语序不当" tabindex="-1">1 关联词语序不当 <a class="header-anchor" href="#_1-关联词语序不当" aria-label="Permalink to “1 关联词语序不当”">​</a></h3><p>带关联词的复句（句子），</p><ul><li>如果两个分句的主语<strong>一致</strong>，则先说主语，后说关联词。另一个分句的主语可省略。</li><li>如果两个分句的主语<strong>不一致</strong>，则先说关联词，再说主语。此时分句的主语不能省略。</li></ul><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>有时候关联词会连用。当一组关联词结束后，才会接另一组关联词。</p></div><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>碰到关联词“也”和“就”，先写主语，后写“也”和“就”。</p></div><h3 id="_2-多项定语、状语语序不当" tabindex="-1">2 多项定语、状语语序不当 <a class="header-anchor" href="#_2-多项定语、状语语序不当" aria-label="Permalink to “2 多项定语、状语语序不当”">​</a></h3><p><strong>多项定语的正确次序一般是：</strong></p><ol><li>表示领属性的或时间的、处所的</li><li>数量的短语</li><li>动词或动词短语（“有”是动词，比如“他有一条狗”。“有”可以理解为“持有”，是动词。）</li><li>形容词或形容词短语</li><li>名词或名词短语</li></ol><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-24-16-24-30.png" alt="picgo-2025-01-24-16-24-30"><p><strong>多项状语的正确次序一般是：</strong></p><ol><li>表目的或原因的短语</li><li>时间词</li><li>处所词</li><li>形容词</li><li>对象</li></ol><h3 id="_3-语义不明" tabindex="-1">3 语义不明 <a class="header-anchor" href="#_3-语义不明" aria-label="Permalink to “3 语义不明”">​</a></h3><p>多由于过个修饰主语的定语有多个，造成歧义。</p><h2 id="歌" tabindex="-1">歌 <a class="header-anchor" href="#歌" aria-label="Permalink to “歌”">​</a></h2><p>对于冬天依然上课的同学的一个祝福、寄托的歌。</p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-23-17-53-49.png" alt="picgo-2025-01-23-17-53-49">`,19)])}var u=o(c,[[`render`,l]]);export{s as __pageData,u as default};