import{S as e,U as t,q as n,t as r,w as i,x as a}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as o}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";r();const s=JSON.parse(`{"title":"不留退路，方能凝志向前","description":"","frontmatter":{"label":["作文"],"level":null},"headers":[{"level":2,"title":"范文","slug":"范文","link":"#范文","children":[]},{"level":2,"title":"答","slug":"答","link":"#答","children":[]}],"relativePath":"gaozhong/01_语文/题/作文/01不留退路，方能凝志向前.md","filePath":"gaozhong/01_语文/题/作文/01不留退路，方能凝志向前.md","lastUpdated":1758138895000}`);var c={name:`gaozhong/01_语文/题/作文/01不留退路，方能凝志向前.md`};function l(r,o,s,c,l,u){let d=n(`ShowLabels`);return t(),a(`div`,null,[i(d),o[0]||=e(`<h1 id="不留退路-方能凝志向前" tabindex="-1">不留退路，方能凝志向前 <a class="header-anchor" href="#不留退路-方能凝志向前" aria-label="Permalink to “不留退路，方能凝志向前”">​</a></h1><p>一个年轻人拜船工为师学划船，船工要求他先学会游泳。</p><p>年轻人问为什么，船工说： “如果你不会游泳，那么你在划船时就会<span class="text-red">担心</span>自己失足落水，而一旦有了这个<span class="text-red">担忧</span>，就<span class="text-red">难于专心致志</span>地划船了。”</p><p>年轻人说： “我不会游泳，学划船时就会<span class="text-red">心怀忧虑</span>， <span class="text-red">小心翼翼，不敢犯错</span>，这样不是会<span class="text-red">学得更认真更快</span>吗？ ”</p><p>要求选好角度，确定立意，明确文体，自拟题目；不要脱离材料内容及含意的范围作文，不要套作， 不得抄袭。</p><details class="details custom-block"><summary>立意（树立观点）</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-14-14-35-39.jpeg" alt="picgo-2025-02-14-14-35-39"></details><h2 id="范文" tabindex="-1">范文 <a class="header-anchor" href="#范文" aria-label="Permalink to “范文”">​</a></h2><details class="details custom-block"><summary>Details</summary><p><strong>不留退路，方能凝志向前</strong></p><p>巨鹿河边破釜沉舟，项籍不留退路，凝志向前，终一战功成；驱散家人抬棺上奏，海瑞不留退路，凝志向前，终感动帝王；应县塔顶挚挂悬空，梁思成不留退路，凝志向前，终绘制绝景。不会游泳，等于断了退路，在学习划船之时只能更加专注，当可更快熟谙御舟之秘奥。年轻人的话惹人深思，也为我们摘示了一大信条：不留退路，方能凝志向前。</p><p>逆境中，不留退路，方能凝志向前，转命运之轮。当生命的转轮在逆境中陷入泥淖之时，不留退路，可以帮助我们一心向前，最终摆脱。出身卑微，天资平庸，从农村走来的俞敏洪 本注定平凡，然而不留退路的精神最终让他终凝志向前。在无法忍受庸常琐碎的生活之后， 他毅然辞职离开北大，并投上自己的所有积蓄创办了新东方学校。后路已断，他唯有临深履薄，史加专注地走好每一步，于是一段商业传奇就此上演。就这样，这位始终坚信“从绝望中寻 找希望，人生终将辉煌”的普通人，因为自己的不留退路的精神而凝志向前，反转了命运之抡，成为了一代学子心中崇敬的“留学教父”。</p><p>险境中，不留退路，方能凝志向前，锦卓伟之业。在险要处境中，留有退路的话，会让我 们总是回顾退路而无法专心化解危难。康熙皇帝，14 岁时便许下“唯愿天下义安，生民乐业”之宏愿，为将之兑现，他不留退路，凝志向前一败则国倾，他凝志布局，削平三藩；输则板荡， 他凝志用人，收复台湾；挫则朝危，他凝志运筹，剿灭葛尔丹。最终，生民乐于樵苏，免受兵燹；盛世永留人间，太平美满。正是因为这位清圣祖一生不留退路，凝志向前，才能于在位的 61 年中绘制了那“万民康宁、天下熙盛”的盛世画卷，也使得自己成为“千古一帝”，站在了历史的风口浪尖紧握住日月旋转。</p><p>孤境中，不留退路，方能凝志向前，创万世之名。在孤绝处境下，背后的退路会让我们畏意不前，最终沦于平庸，寂寂无名。在那个君王怠政，宦官擅权的晚明时代，无锡东林书院 的学人，为对抗阉党，不留后路，凝志向前。他们恪守着创始人顾宪成题写在书院门前的“家事国事天下事，事事关心”的楹联精神，在魏忠贤权势滔天之时联名上书，弹劾宦寺。虽然 最终“东林六君子”惨遭戕害，书院被毁，但这种不留后路，凝志向前的精神还是给他们带来了数百年后美国学者口中“一支重整道德的十字军”的嘉誉，帮助他们创下万世之名，成为了夜空中最璀璨的星群。</p><p>人们总说有备无患，人们总说给自己留一条退路。可是，只有像材料中的年轻人一样洞悉 人性的真的智者才明白，退路越多，倾力投入的可能性越小，只有自断退路，才能激发全部潜能，专注于每一步，最终转动命运之轮，铸就卓伟之业，创得万世之名。听，那回荡在釜破舟沉 的鸣响中的；触，那凿刻在为己而备的棺木里的；看，那高悬在应县木塔的铁链上的——历史的星空中，总有无数先贤，用他们的足迹向我们昭示着：</p><p>不留退路，方能凝志向前。</p></details><h2 id="答" tabindex="-1">答 <a class="header-anchor" href="#答" aria-label="Permalink to “答”">​</a></h2><p>立意（树立观点）：</p><p>船工角度：</p><ol><li>内心有担忧，就难专心</li><li>给自己留后路，才能专心做事</li></ol><p>年轻人角度：</p><ol><li>内心有担忧（有忧患意识），更能凝志向前</li><li>不给自己留后路，才能破釜沉舟、凝志向前、做事。</li></ol><blockquote><p>似乎年轻人的角度更好写。</p></blockquote><details class="details custom-block"><summary>练习文 1</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-16-03-42-36.png" alt="picgo-2025-01-16-03-42-36"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-16-03-42-51.png" alt="picgo-2025-01-16-03-42-51"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-16-03-43-00.png" alt="picgo-2025-01-16-03-43-00"></details><details class="details custom-block"><summary>练习文 2</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-16-02-10-12.jpeg" alt="picgo-2025-02-16-02-10-12"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-16-02-10-38.jpeg" alt="picgo-2025-02-16-02-10-38"></details>`,17)])}var u=o(c,[[`render`,l]]);export{s as __pageData,u as default};