import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"01_提能力·专题归纳","description":"","frontmatter":{},"headers":[{"level":2,"title":"专题 1 高考中的重要模型——鳖膈","slug":"专题-1-高考中的重要模型——鳖膈","link":"#专题-1-高考中的重要模型——鳖膈","children":[{"level":3,"title":"1 考题再现","slug":"_1-考题再现","link":"#_1-考题再现","children":[]},{"level":3,"title":"2 教材探源","slug":"_2-教材探源","link":"#_2-教材探源","children":[]},{"level":3,"title":"3 鳖膈的来源探索","slug":"_3-鳖膈的来源探索","link":"#_3-鳖膈的来源探索","children":[]},{"level":3,"title":"4 鳖膈的性质","slug":"_4-鳖膈的性质","link":"#_4-鳖膈的性质","children":[]},{"level":3,"title":"5 鳖膈模型在高考中的应用","slug":"_5-鳖膈模型在高考中的应用","link":"#_5-鳖膈模型在高考中的应用","children":[]}]},{"level":2,"title":"专题 2 翻折视角下的全国卷立体几何试题","slug":"专题-2-翻折视角下的全国卷立体几何试题","link":"#专题-2-翻折视角下的全国卷立体几何试题","children":[{"level":3,"title":"1 筝形的翻折","slug":"_1-筝形的翻折","link":"#_1-筝形的翻折","children":[]},{"level":3,"title":"2 梯形的翻折","slug":"_2-梯形的翻折","link":"#_2-梯形的翻折","children":[]},{"level":3,"title":"3 矩形 (正方形) 的翻折","slug":"_3-矩形-正方形-的翻折","link":"#_3-矩形-正方形-的翻折","children":[]}]},{"level":2,"title":"专题 3 以模型为载体解决空间几何体的外接球问题","slug":"专题-3-以模型为载体解决空间几何体的外接球问题","link":"#专题-3-以模型为载体解决空间几何体的外接球问题","children":[{"level":3,"title":"模型一 墙角模型 (三条棱两两垂直，不找球心的位置即可求出球半径)","slug":"模型一-墙角模型-三条棱两两垂直-不找球心的位置即可求出球半径","link":"#模型一-墙角模型-三条棱两两垂直-不找球心的位置即可求出球半径","children":[]},{"level":3,"title":"模型二 汉堡模型 (直棱柱的外接球、圆柱的外接球)","slug":"模型二-汉堡模型-直棱柱的外接球、圆柱的外接球","link":"#模型二-汉堡模型-直棱柱的外接球、圆柱的外接球","children":[]},{"level":3,"title":"模型三 切瓜模型 (正弦定理求大圆直径是通法)","slug":"模型三-切瓜模型-正弦定理求大圆直径是通法","link":"#模型三-切瓜模型-正弦定理求大圆直径是通法","children":[]}]}],"relativePath":"gaozhong/02_数学/01_自己总结基础知识/07_立体几何初步/00_复习/01_提能力·专题归纳.md","filePath":"gaozhong/02_数学/01_自己总结基础知识/07_立体几何初步/00_复习/01_提能力·专题归纳.md","lastUpdated":1758138895000}`);var o={name:`gaozhong/02_数学/01_自己总结基础知识/07_立体几何初步/00_复习/01_提能力·专题归纳.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="_01-提能力·专题归纳" tabindex="-1">01_提能力·专题归纳 <a class="header-anchor" href="#_01-提能力·专题归纳" aria-label="Permalink to “01_提能力·专题归纳”">​</a></h1><h2 id="专题-1-高考中的重要模型——鳖膈" tabindex="-1">专题 1 高考中的重要模型——鳖膈 <a class="header-anchor" href="#专题-1-高考中的重要模型——鳖膈" aria-label="Permalink to “专题 1 高考中的重要模型——鳖膈”">​</a></h2><p>中国古代数学强调实用性，很多古代数学问题都结合实际生 活存在于典籍中。其中在对立体几何的研究中最重要的模型 便是由立方体截得的三棱锥和四棱锥，它们在古代有专门的 名词：鳖膈和阳马.2015 年湖北高考便以此为背景命题，一时 间引起了广泛的关注。</p><h3 id="_1-考题再现" tabindex="-1">1 考题再现 <a class="header-anchor" href="#_1-考题再现" aria-label="Permalink to “1 考题再现”">​</a></h3><h4 id="例-1" tabindex="-1">例 1 <a class="header-anchor" href="#例-1" aria-label="Permalink to “例 1”">​</a></h4><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-20-27.png" alt="picgo-2025-02-23-16-20-27"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-20-53.png" alt="picgo-2025-02-23-16-20-53"><h3 id="_2-教材探源" tabindex="-1">2 教材探源 <a class="header-anchor" href="#_2-教材探源" aria-label="Permalink to “2 教材探源”">​</a></h3><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-21-24.png" alt="picgo-2025-02-23-16-21-24"><h3 id="_3-鳖膈的来源探索" tabindex="-1">3 鳖膈的来源探索 <a class="header-anchor" href="#_3-鳖膈的来源探索" aria-label="Permalink to “3 鳖膈的来源探索”">​</a></h3><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-21-46.png" alt="picgo-2025-02-23-16-21-46"><h3 id="_4-鳖膈的性质" tabindex="-1">4 鳖膈的性质 <a class="header-anchor" href="#_4-鳖膈的性质" aria-label="Permalink to “4 鳖膈的性质”">​</a></h3><h4 id="_1-聚焦于立体几何中的核心关系——垂直关系" tabindex="-1">(1) 聚焦于立体几何中的核心关系——垂直关系 <a class="header-anchor" href="#_1-聚焦于立体几何中的核心关系——垂直关系" aria-label="Permalink to “(1) 聚焦于立体几何中的核心关系——垂直关系”">​</a></h4><h5 id="例-2" tabindex="-1">例 2 <a class="header-anchor" href="#例-2" aria-label="Permalink to “例 2”">​</a></h5><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-22-39.png" alt="picgo-2025-02-23-16-22-39"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-22-50.png" alt="picgo-2025-02-23-16-22-50"><h4 id="_2-鳖膈的空间角" tabindex="-1">(2) 鳖膈的空间角 <a class="header-anchor" href="#_2-鳖膈的空间角" aria-label="Permalink to “(2) 鳖膈的空间角”">​</a></h4><h5 id="例-3" tabindex="-1">例 3 <a class="header-anchor" href="#例-3" aria-label="Permalink to “例 3”">​</a></h5><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-23-18.png" alt="picgo-2025-02-23-16-23-18"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-23-42.png" alt="picgo-2025-02-23-16-23-42"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-24-04.png" alt="picgo-2025-02-23-16-24-04"><h3 id="_5-鳖膈模型在高考中的应用" tabindex="-1">5 鳖膈模型在高考中的应用 <a class="header-anchor" href="#_5-鳖膈模型在高考中的应用" aria-label="Permalink to “5 鳖膈模型在高考中的应用”">​</a></h3><p>鳖膈模型覆盖了立体几何中线、面的各种垂直关系及各种空 间角的计算，是研究立体几何问题的基本图形，在高考中有着 广泛的应用。</p><h4 id="例-4" tabindex="-1">例 4 <a class="header-anchor" href="#例-4" aria-label="Permalink to “例 4”">​</a></h4><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-25-09.png" alt="picgo-2025-02-23-16-25-09"><h4 id="例-5" tabindex="-1">例 5 <a class="header-anchor" href="#例-5" aria-label="Permalink to “例 5”">​</a></h4><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-25-25.png" alt="picgo-2025-02-23-16-25-25"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-25-35.png" alt="picgo-2025-02-23-16-25-35"><h2 id="专题-2-翻折视角下的全国卷立体几何试题" tabindex="-1">专题 2 翻折视角下的全国卷立体几何试题 <a class="header-anchor" href="#专题-2-翻折视角下的全国卷立体几何试题" aria-label="Permalink to “专题 2 翻折视角下的全国卷立体几何试题”">​</a></h2><p>高考全国卷中，立体几何重视考查几何元素间的位置关系、度 量关系。回顾和审视全国卷历年试题的命制，我们发现有一条 清晰的脉络，那就是特别重视基本平面几何图形性质的空间 探索，也即特别重视平面图形翻折前后的几何元素间的位置 关系、度量关系的变与不变的考查。这样命题有助于充分考查 考生的空间想象能力，有助于从熟悉的平面图形要素的位置 关系进入到空间几何体的几何要素关系的把握上</p><h3 id="_1-筝形的翻折" tabindex="-1">1 筝形的翻折 <a class="header-anchor" href="#_1-筝形的翻折" aria-label="Permalink to “1 筝形的翻折”">​</a></h3><p>筝形是指以一条对角线所在直线为对称轴的四边形，与菱形 定义相对应。菱形是特殊的筝形。筝形的一条对角线所在的直 线垂直平分另一条对角线. 在筝形从平面到空间变换的研究中，常常沿着其中一条对角 线进行翻折。在翻折过程中，两条对角线垂直关系保持不变，这 就成为高考试题命制的基础，常常利用两个对应的等腰三角 形来描述空间筝形。这样的高考试题极为丰富</p><h4 id="例-6" tabindex="-1">例 6 <a class="header-anchor" href="#例-6" aria-label="Permalink to “例 6”">​</a></h4><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-26-19.png" alt="picgo-2025-02-23-16-26-19"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-26-36.png" alt="picgo-2025-02-23-16-26-36"><h4 id="例-7" tabindex="-1">例 7 <a class="header-anchor" href="#例-7" aria-label="Permalink to “例 7”">​</a></h4><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-27-05.png" alt="picgo-2025-02-23-16-27-05"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-27-23.png" alt="picgo-2025-02-23-16-27-23"><h3 id="_2-梯形的翻折" tabindex="-1">2 梯形的翻折 <a class="header-anchor" href="#_2-梯形的翻折" aria-label="Permalink to “2 梯形的翻折”">​</a></h3><p>梯形是学生较为熟悉的平面四边形，虽然初中阶段依然弱化 对梯形的性质研究，但在高中阶段，其仍然是重要的平面四边 形 (当然也可以从图形拼接的角度来看待梯形),其中，等腰梯 形和直角梯形是重要的研究对象</p><h4 id="_2-1-等腰梯形的翻折" tabindex="-1">2.1 等腰梯形的翻折 <a class="header-anchor" href="#_2-1-等腰梯形的翻折" aria-label="Permalink to “2.1 等腰梯形的翻折”">​</a></h4><p>等腰梯形的翻折主要强调对腰的翻折，也即保持底面的矩形 特征，两腰向中间翻折，而这里面就有两底的端点是否合拢的 问题。下面我们以高考试题为例分别研究上述两类问题。</p><h5 id="例-8" tabindex="-1">例 8 <a class="header-anchor" href="#例-8" aria-label="Permalink to “例 8”">​</a></h5><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-28-21.png" alt="picgo-2025-02-23-16-28-21"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-28-37.png" alt="picgo-2025-02-23-16-28-37"><h5 id="例-9" tabindex="-1">例 9 <a class="header-anchor" href="#例-9" aria-label="Permalink to “例 9”">​</a></h5><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-29-08.png" alt="picgo-2025-02-23-16-29-08"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-29-25.png" alt="picgo-2025-02-23-16-29-25"><h5 id="例-10" tabindex="-1">例 10 <a class="header-anchor" href="#例-10" aria-label="Permalink to “例 10”">​</a></h5><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-29-45.png" alt="picgo-2025-02-23-16-29-45"><h3 id="_3-矩形-正方形-的翻折" tabindex="-1">3 矩形 (正方形) 的翻折 <a class="header-anchor" href="#_3-矩形-正方形-的翻折" aria-label="Permalink to “3 矩形 (正方形) 的翻折”">​</a></h3><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-30-01.png" alt="picgo-2025-02-23-16-30-01"><p>类比于筝形，在图形沿着对角线 BD 或者 CF 翻折过程中，垂直 关系始终保持不变，而这就是命题的落脚点。近几年全国卷高 考题也进行了正方形的翻折研究</p><h4 id="例-11" tabindex="-1">例 11 <a class="header-anchor" href="#例-11" aria-label="Permalink to “例 11”">​</a></h4><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-30-42.png" alt="picgo-2025-02-23-16-30-42"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-31-05.png" alt="picgo-2025-02-23-16-31-05"><h4 id="例-12" tabindex="-1">例 12 <a class="header-anchor" href="#例-12" aria-label="Permalink to “例 12”">​</a></h4><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-31-23.png" alt="picgo-2025-02-23-16-31-23"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-31-36.png" alt="picgo-2025-02-23-16-31-36"><h4 id="例-13" tabindex="-1">例 13 <a class="header-anchor" href="#例-13" aria-label="Permalink to “例 13”">​</a></h4><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-32-00.png" alt="picgo-2025-02-23-16-32-00"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-32-26.png" alt="picgo-2025-02-23-16-32-26"><h2 id="专题-3-以模型为载体解决空间几何体的外接球问题" tabindex="-1">专题 3 以模型为载体解决空间几何体的外接球问题 <a class="header-anchor" href="#专题-3-以模型为载体解决空间几何体的外接球问题" aria-label="Permalink to “专题 3 以模型为载体解决空间几何体的外接球问题”">​</a></h2><p>球是特殊的几何体，具有很多特殊的性质。空间几何体的外接 球问题是高考的热点和难点。以考查学生的空间想象能力为 主线，结合边角关系、位置关系、面积与体积的计算，从而达到 培养学生直观想象的核心素养的要求。从教学实践中发现学 生在研究空间几何体的外接球问题时常常因缺乏空间想象力 而感到束手无策，其根本原因是学生没有形成解题的模式和 套路，以至于遇到类似的题目便产生畏惧心理。以下结合教学 实例，给出解空间几何体外接球问题的三种模型及其方法</p><h3 id="模型一-墙角模型-三条棱两两垂直-不找球心的位置即可求出球半径" tabindex="-1">模型一 墙角模型 (三条棱两两垂直，不找球心的位置即可求出球半径) <a class="header-anchor" href="#模型一-墙角模型-三条棱两两垂直-不找球心的位置即可求出球半径" aria-label="Permalink to “模型一 墙角模型 (三条棱两两垂直，不找球心的位置即可求出球半径)”">​</a></h3><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-33-07.png" alt="picgo-2025-02-23-16-33-07"><h4 id="例-14" tabindex="-1">例 14 <a class="header-anchor" href="#例-14" aria-label="Permalink to “例 14”">​</a></h4><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-33-29.png" alt="picgo-2025-02-23-16-33-29"><h3 id="模型二-汉堡模型-直棱柱的外接球、圆柱的外接球" tabindex="-1">模型二 汉堡模型 (直棱柱的外接球、圆柱的外接球) <a class="header-anchor" href="#模型二-汉堡模型-直棱柱的外接球、圆柱的外接球" aria-label="Permalink to “模型二 汉堡模型 (直棱柱的外接球、圆柱的外接球)”">​</a></h3><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-33-48.png" alt="picgo-2025-02-23-16-33-48"><h4 id="例-15" tabindex="-1">例 15 <a class="header-anchor" href="#例-15" aria-label="Permalink to “例 15”">​</a></h4><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-34-06.png" alt="picgo-2025-02-23-16-34-06"><h3 id="模型三-切瓜模型-正弦定理求大圆直径是通法" tabindex="-1">模型三 切瓜模型 (正弦定理求大圆直径是通法) <a class="header-anchor" href="#模型三-切瓜模型-正弦定理求大圆直径是通法" aria-label="Permalink to “模型三 切瓜模型 (正弦定理求大圆直径是通法)”">​</a></h3><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-34-34.png" alt="picgo-2025-02-23-16-34-34"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-34-54.png" alt="picgo-2025-02-23-16-34-54"><h4 id="例-16" tabindex="-1">例 16 <a class="header-anchor" href="#例-16" aria-label="Permalink to “例 16”">​</a></h4><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-23-16-35-09.png" alt="picgo-2025-02-23-16-35-09">`,77)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};