import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"语法填空考点大串讲","description":"","frontmatter":{},"headers":[{"level":2,"title":"名词","slug":"名词","link":"#名词","children":[]},{"level":2,"title":"谓语动词","slug":"谓语动词","link":"#谓语动词","children":[]},{"level":2,"title":"非谓语动词：形意对应","slug":"非谓语动词-形意对应","link":"#非谓语动词-形意对应","children":[]},{"level":2,"title":"形容词","slug":"形容词","link":"#形容词","children":[]},{"level":2,"title":"副词","slug":"副词","link":"#副词","children":[]},{"level":2,"title":"代词：代词靠翻译","slug":"代词-代词靠翻译","link":"#代词-代词靠翻译","children":[]},{"level":2,"title":"冠词","slug":"冠词","link":"#冠词","children":[]},{"level":2,"title":"介词","slug":"介词","link":"#介词","children":[]},{"level":2,"title":"连词","slug":"连词","link":"#连词","children":[]},{"level":2,"title":"并列","slug":"并列","link":"#并列","children":[]},{"level":2,"title":"固定搭配","slug":"固定搭配","link":"#固定搭配","children":[]}],"relativePath":"gaozhong/03_英语/02_自己总结/01_《高考英语解题大招》/01 语法填空/语法填空考点大串讲.md","filePath":"gaozhong/03_英语/02_自己总结/01_《高考英语解题大招》/01 语法填空/语法填空考点大串讲.md","lastUpdated":1741447220000}`);var o={name:`gaozhong/03_英语/02_自己总结/01_《高考英语解题大招》/01 语法填空/语法填空考点大串讲.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="语法填空考点大串讲" tabindex="-1">语法填空考点大串讲 <a class="header-anchor" href="#语法填空考点大串讲" aria-label="Permalink to “语法填空考点大串讲”">​</a></h1><h2 id="名词" tabindex="-1">名词 <a class="header-anchor" href="#名词" aria-label="Permalink to “名词”">​</a></h2><ul><li><p>（1）名词单复数文中必有提示 eg：several/few/动词原形/are</p></li><li><p>（2）动词变名词</p><ol><li><p>动词是及物动词 educate</p><ol><li>不跟名词变名词 Education (educate) is important</li><li>跟名词变动名词 Educating (educate) students is important</li></ol></li><li><p>动词是不及物动词 die -&gt; dying/death</p><p>看修饰成分的位置</p><ol><li>We should avoid early death (die)</li><li>We should avoid dying (die) early</li></ol></li></ol></li></ul><h2 id="谓语动词" tabindex="-1">谓语动词 <a class="header-anchor" href="#谓语动词" aria-label="Permalink to “谓语动词”">​</a></h2><ul><li>（1）主被动 <ol><li>be done/get done</li><li>情态动词 + be done</li></ol></li><li>（2）时态：“形意对应” <ol><li>常态：do/does</li><li>讲故事：did（动作=过去，句子=过去）</li><li>吹牛逼：have/has done（动作=过去，句子=现在） <ol><li>完成时 + （since + 过去时）</li><li>so far/by now/up till now/up to now 到现在为止</li><li>over the last few years/during the past several weeks/in the last 3 days</li><li>次数： <ol><li>I have done sth. 3 times</li><li>It is the third time that I have done sth.</li></ol></li></ol></li><li>发生在 did 之前：had done</li><li>发生在 did 的将来：would do</li><li>将要做某事/将来会发生：will do</li><li>将做完某事/某事将完成：will have done</li><li>“正在”：be doing</li><li>“一直一直一直一直做已经做了很长时间还在做”：have/has/had been doing</li></ol></li></ul><h2 id="非谓语动词-形意对应" tabindex="-1">非谓语动词：形意对应 <a class="header-anchor" href="#非谓语动词-形意对应" aria-label="Permalink to “非谓语动词：形意对应”">​</a></h2><ul><li>（1）做 doing</li><li>（2）被做 done</li><li>（3）要做去做还未做 to do <ol><li><strong>形容词（喜怒哀乐惊讶好坏难易贵贱） + to do</strong></li><li>only to do = 令人惊讶的结果</li><li>I have homework to do 自己做。I have homework to be done 别人做。</li></ol></li><li>（4）要被做 to be done</li><li>（5）之前做 having done</li><li>（6）之前被做 having been done</li><li>（7）正在被做 being done</li></ul><h2 id="形容词" tabindex="-1">形容词 <a class="header-anchor" href="#形容词" aria-label="Permalink to “形容词”">​</a></h2><ul><li>（1）形容词在哪儿用？ <ol><li>修饰名词——作定语！eg：a handsome teacher</li><li>系动词后——作标语！eg：Li Hui is handsome</li></ol></li><li>（2）形容词考什么？ <ol><li>名词变形容词：后缀</li><li>动词变形容词：“使动词”用法 excite -&gt; <ol><li>+ing 表“令人”exciting 令人兴奋的 adj.</li><li>+ed 表“感到”excited 感到兴奋的 adj.</li></ol></li><li>形容词的比较级 <ol><li>碰到 as 选原级 Li Hui is as handsome as LiuDehua</li><li>碰到 than 选比较级 Li Hui is more handsome than LiuDehua</li><li>碰到 the 选最高级（in/of/among）（eg：Li Hui is the most handsome teacher of/among all teachers in the world）</li></ol></li></ol></li></ul><h2 id="副词" tabindex="-1">副词 <a class="header-anchor" href="#副词" aria-label="Permalink to “副词”">​</a></h2><ul><li><p>（1）副词在哪儿用？（重要！！！）</p><ol><li>修饰动词 Li Hui really teaches English</li><li>修饰形容词副词 <ol><li>Li Hui looks really handsome</li><li>Li Hui speaks really fast</li></ol></li><li>修饰介短 Li Hui is really in Beijing</li><li>修饰整句 Fortunately, I made up my mind to learn English with Li Hui</li></ol></li><li><p>（2）副词考什么？</p><ol><li>形容词 + ly = 副词</li><li>副词的比较登记 <ol><li>碰到 as 选原级</li><li>碰到 than 选比较级</li><li>碰到 the 选最高级（in/of/among 搭配）</li></ol></li><li>修饰比较级：</li></ol><p>for, a little, a bit, much, a lot, a great deal, any, even, still, slightly</p><p>eg：Li Hua is a lot more handsome than Liu Dehua</p></li></ul><h2 id="代词-代词靠翻译" tabindex="-1">代词：代词靠翻译 <a class="header-anchor" href="#代词-代词靠翻译" aria-label="Permalink to “代词：代词靠翻译”">​</a></h2><ul><li>（1）我（主）I</li><li>（2）我（宾）me</li><li>（3）我的（形容词）my</li><li>（4）我的（名词）mine</li><li>（5）我自己 myself（反身代词：主语发出动作作用于主语本身）</li></ul><h2 id="冠词" tabindex="-1">冠词 <a class="header-anchor" href="#冠词" aria-label="Permalink to “冠词”">​</a></h2><ul><li><p>（1）翻译法</p><ol><li>一/个/一个：a/an</li><li>这/内：the</li></ol><p>eg：Look！There is a cat under the desk</p></li><li><p>（2）特指泛指分析法</p><ol><li><p>特指：明确知道是哪个（The sun is shining in the sky）</p></li><li><p>泛指：随便哪个都行/一类中的一个</p><p>I need a student to help me</p><p>Li Hui is a eeacher</p></li></ol></li><li><p>（3）固定搭配</p><ol><li>有 the 表具象，无 the 表抽象 <ol><li>in the hospital 在医院里/ in hospital 在住院</li><li>play the piano 弹的是钢琴本身/ play football 玩的是一套游戏</li></ol></li><li>有 the 有限制，无 the 无限制 <ol><li>in the front of the bus 在公交车前部分</li><li>in front of the bus 在公交车外面的前面。</li></ol></li></ol></li></ul><h2 id="介词" tabindex="-1">介词 <a class="header-anchor" href="#介词" aria-label="Permalink to “介词”">​</a></h2><p>（1）基本含义</p><ol><li>in 范围里</li><li>at 点</li><li>on 吸附</li><li>off 断开</li><li>to 终点</li><li>from 离开</li><li>for 为/花</li><li>with 伴随着</li><li>by 倚靠/乘坐/被</li><li>about 围绕/大约</li></ol><p>（2）固定搭配</p><h2 id="连词" tabindex="-1">连词 <a class="header-anchor" href="#连词" aria-label="Permalink to “连词”">​</a></h2><p>（1）并列连词</p><ol><li>and 和、并且、然后、那么</li><li>or 或则、否则</li><li>but 但是、除了</li><li>so 所以、以便</li></ol><p>（2）三大从句</p><ol><li>名词性从句：“的那个”翻译大法（What I said）is true <ol><li><p>连词：从句完整填连词或副词</p><ol><li>that 说/不翻译/这件事 I know you love me</li><li>whether 是否/与否 I wonder whether you love me</li></ol></li><li><p>连接副词：从句完整填连词或副词</p><ol><li>when 生么时候/……的那个时间</li><li>where 什么地方/……的那个地方</li><li>why 为什么/……的那个原因</li><li>how 怎么/多么/……的那个方式</li></ol><p>eg：I don&#39;t konw when/where/why/how/whether/if he will go</p></li><li><p>连接代词：从句不完整填代词</p><ol><li>who 谁/……的那个人</li><li>whom 谁/……的那个人</li><li>which 哪个/……的其中一个</li><li>what 什么/……的那个东西/……的那个事儿</li></ol><p>eg：I don‘t know who/which/what he likes</p></li><li><p>特殊注意事项：</p><ol><li>加上 ever 表示“无论”</li><li>“how + adj/adv”引导名从 <ol><li>I don&#39;t know （how handsome Li Hua looks）</li><li>I don&#39;t know （how fast Li Hui speaks）</li></ol></li><li>&quot;what/which + n.&quot; 引导名从 <ol><li>I don&#39;t know (which fruit you like)</li><li>I don&#39;t know (which student broke the glass)</li></ol></li></ol></li></ol></li><li>定语从句：考引导词的选择 <ol><li><p>特殊关系词：靠翻译搞定</p><ol><li>“正如”：as。eg：As our teacher told us，Li Hui is handsome</li><li>“他的、它们的”：whose。eg：I have a house whose windows face the sea</li><li>“为什么”why（why 的唯一先行词是 reason）</li></ol><p>eg：I want to know the reason why Li Hui teaches English</p></li><li><p>关系代词：从句不完整，填代词</p><ol><li>空格前没逗号没介词，填 that</li><li>有逗号，人 who，物 which</li><li>有介词，人 whom，物 which</li></ol></li><li><p>关系副词：从句完整，填副词</p><ol><li>when“那会儿”（修饰时间名词）</li><li>where“在那儿”（修饰其他名词）“非时间即地点”</li></ol><p>eg：I love the job（where I can help people）</p></li><li><p>特殊注意事项</p><ol><li>Those（who） = 那么什么什么样的人……</li><li>某人做某事的方式 <ol><li>The way sb. do sth...</li><li>The way that sb. do sth...</li><li>The way in which sb. do sth...</li></ol></li></ol></li></ol></li><li>状语从句：背引导词！！ <ol><li>as if/ as though 就好像</li><li>even if/ even though 即使</li><li>as long as 只要</li><li>only if 只有如果</li><li>if only 要是……就好了</li></ol></li></ol><h2 id="并列" tabindex="-1">并列 <a class="header-anchor" href="#并列" aria-label="Permalink to “并列”">​</a></h2><p>只要空格附近有 and，则 and 一定是线索——必须搞清楚谁跟谁并列。</p><p>（1）如果 A and B，则 A = B</p><p>（2）先找 B，后找 A</p><h2 id="固定搭配" tabindex="-1">固定搭配 <a class="header-anchor" href="#固定搭配" aria-label="Permalink to “固定搭配”">​</a></h2><p>只考常见的。</p>`,30)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};