import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"阅读七选五","description":"","frontmatter":{},"headers":[{"level":2,"title":"1 选项特征","slug":"_1-选项特征","link":"#_1-选项特征","children":[{"level":3,"title":"1“半句话”选项","slug":"_1-半句话-选项","link":"#_1-半句话-选项","children":[]},{"level":3,"title":"2“问句”选项","slug":"_2-问句-选项","link":"#_2-问句-选项","children":[]},{"level":3,"title":"3“祈使句”选项","slug":"_3-祈使句-选项","link":"#_3-祈使句-选项","children":[]},{"level":3,"title":"4“名词”选项","slug":"_4-名词-选项","link":"#_4-名词-选项","children":[]},{"level":3,"title":"5“数字”选项","slug":"_5-数字-选项","link":"#_5-数字-选项","children":[]},{"level":3,"title":"6“代词”选项","slug":"_6-代词-选项","link":"#_6-代词-选项","children":[]},{"level":3,"title":"7“逻辑词”选项","slug":"_7-逻辑词-选项","link":"#_7-逻辑词-选项","children":[]},{"level":3,"title":"8“总 — 分”选项","slug":"_8-总-—-分-选项","link":"#_8-总-—-分-选项","children":[]}]},{"level":2,"title":"2 话题一致","slug":"_2-话题一致","link":"#_2-话题一致","children":[]}],"relativePath":"gaozhong/03_英语/02_自己总结/01_《高考英语解题大招》/03 阅读七选五/01 两大做题核心思想.md","filePath":"gaozhong/03_英语/02_自己总结/01_《高考英语解题大招》/03 阅读七选五/01 两大做题核心思想.md","lastUpdated":1758138895000}`);var o={name:`gaozhong/03_英语/02_自己总结/01_《高考英语解题大招》/03 阅读七选五/01 两大做题核心思想.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="阅读七选五" tabindex="-1">阅读七选五 <a class="header-anchor" href="#阅读七选五" aria-label="Permalink to “阅读七选五”">​</a></h1><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-10-16-59.png" alt="picgo-2025-01-31-10-16-59"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-10-18-10.png" alt="picgo-2025-01-31-10-18-10"><h2 id="_1-选项特征" tabindex="-1">1 选项特征 <a class="header-anchor" href="#_1-选项特征" aria-label="Permalink to “1 选项特征”">​</a></h2><p>特征：选项是半句话，不具备完整的句子结构。</p><p>解法：选项应填入原文中“逗号”前面的空格处。</p><h3 id="_1-半句话-选项" tabindex="-1">1“半句话”选项 <a class="header-anchor" href="#_1-半句话-选项" aria-label="Permalink to “1“半句话”选项”">​</a></h3><p><strong>例 1</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-11-41-22.png" alt="picgo-2025-01-31-11-41-22"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-10-42-06.png" alt="picgo-2025-01-31-10-42-06"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-10-42-26.png" alt="picgo-2025-01-31-10-42-26"></details><p><strong>例 2</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-11-41-42.png" alt="picgo-2025-01-31-11-41-42"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-11-42-02.png" alt="picgo-2025-01-31-11-42-02"></details><p><strong>例 3</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-12-00-24.png" alt="picgo-2025-01-31-12-00-24"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-12-00-33.png" alt="picgo-2025-01-31-12-00-33"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-12-05-29.png" alt="picgo-2025-01-31-12-05-29"></details><h3 id="_2-问句-选项" tabindex="-1">2“问句”选项 <a class="header-anchor" href="#_2-问句-选项" aria-label="Permalink to “2“问句”选项”">​</a></h3><p>特征：选项带问号，属于疑问句。</p><p>解法：句式一致（<span class="text-red">优先考虑</span>）；有问有答；</p><p><strong>例 1</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-12-26-19.png" alt="picgo-2025-01-31-12-26-19"><p>“问句”选项通常满足<span class="text-red">“句式一致”</span>原则，共有 2 种情况，即：</p><ol><li>要么空格线前后句都是问句，</li><li>要么各段首句或各段小标题都是问句。</li></ol><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>37 和 40 属于两个干扰选项。</p><p>37 空，有 more than that，表示递进关系，并且后面的<span class="text-red">句式</span>和 B 选项相同。</p><p>说的是怎么让你有动机？并且保持有动机的？</p><p>40 空，后面看起来好像直接回答了 B 选项的问题，但是空格线前半句说的是“你可能想要成为飞行员但是不能够成为因为视力问题”，与“怎么激励你自己”主题不符，</p><p>所以。B 选项填在 37 空。</p></div><p><strong>例 2</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-14-06-40.png" alt="picgo-2025-01-31-14-06-40"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-14-09-00.png" alt="picgo-2025-01-31-14-09-00"></details><p><strong>例 3</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-14-50-13.png" alt="picgo-2025-01-31-14-50-13"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-14-50-32.png" alt="picgo-2025-01-31-14-50-32"><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>有问有答。</p><p>放在 38 处 比 放在 40 处好。</p><p>38 处，问了个问题，然后一个人说我们可以告诉你关于一对年轻人（的故事）</p><p>40 处，问了个问题（所以为什么我们跳舞？），然后接着是说“我发现它很难停止”。因为跳舞很难停止，所以跳舞？感觉逻辑有点说不通。</p><p>所以 A 项放在 38 处。</p></div><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-14-56-08.png" alt="picgo-2025-01-31-14-56-08"></details><p><strong>例 4</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-15-00-02.png" alt="picgo-2025-01-31-15-00-02"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-15-00-15.png" alt="picgo-2025-01-31-15-00-15"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-15-22-07.png" alt="picgo-2025-01-31-15-22-07"></details><h3 id="_3-祈使句-选项" tabindex="-1">3“祈使句”选项 <a class="header-anchor" href="#_3-祈使句-选项" aria-label="Permalink to “3“祈使句”选项”">​</a></h3><p>特征：选项是<strong>祈使句</strong>，即“以<span class="text-red">动词原形开头</span>的句子”。</p><p>解法：</p><ol><li>句式相同——一般空格前后有祈使句，那么中间也是祈使句</li><li>一般<span class="text-red">各段首句</span>或<span class="text-red">各段小标题</span>是祈使句。</li></ol><p><strong>例 1</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-16-10-13.png" alt="picgo-2025-01-31-16-10-13"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-16-19-17.png" alt="picgo-2025-01-31-16-19-17"></details><p><strong>例 2</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-17-11-20.png" alt="picgo-2025-01-31-17-11-20"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-17-11-30.png" alt="picgo-2025-01-31-17-11-30"><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>C 选项属于“祈使句”选项，</p><p>我们浏览各段首句之后，发现各段首句貌似都不是祈使句；</p><p>但是各段首句，虽然都以“When/If”引导的状语从句开头，但其主句部分依然 是 祈 使 句，如 第 三 段 的“have set hours..”、第 四 段 的“don&#39;t do it..”、第 五 段 的“make good use.…&quot;”等，其实都是祈使句!</p><p>所以，第二段首句大概率也是祈使句了!</p><p>而且第二段后面一句也是祈使句。</p></div><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-17-35-50.png" alt="picgo-2025-01-31-17-35-50"></details><p><strong>例 3</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-19-26-51.png" alt="picgo-2025-01-31-19-26-51"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-19-29-26.png" alt="picgo-2025-01-31-19-29-26"></details><p><strong>例 4</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-20-13-19.png" alt="picgo-2025-01-31-20-13-19"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-20-38-45.png" alt="picgo-2025-01-31-20-38-45"></details><p><strong>例 5</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-20-24-01.png" alt="picgo-2025-01-31-20-24-01"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-20-24-13.png" alt="picgo-2025-01-31-20-24-13"><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>根据话题一致，</p><ol start="38"><li>A</li><li>C</li></ol></div><p><strong>例 6</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-23-28-22.png" alt="picgo-2025-01-31-23-28-22"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-01-31-23-32-42.png" alt="picgo-2025-01-31-23-32-42"></details><h3 id="_4-名词-选项" tabindex="-1">4“名词”选项 <a class="header-anchor" href="#_4-名词-选项" aria-label="Permalink to “4“名词”选项”">​</a></h3><p>特征：选项是 一个名词。</p><p>解法：放在文中“应该出现名词”的位置。比如，某段落有词空缺，其他段落的相应位置是名 词，则这个空缺的词大概率就是名词。这种题目出现的时间比较久远，近年只在模拟题中出 现过。</p><p><strong>例 1</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-01-04.png" alt="picgo-2025-02-01-02-01-04"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-11-04-03.png" alt="picgo-2025-02-01-11-04-03"></details><h3 id="_5-数字-选项" tabindex="-1">5“数字”选项 <a class="header-anchor" href="#_5-数字-选项" aria-label="Permalink to “5“数字”选项”">​</a></h3><p>特征：选项中含有数字。</p><p>解法：数字找数字——大多数含有数字的选项，要放在文中有数字的地方。</p><p><strong>例 1</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-01-48.png" alt="picgo-2025-02-01-02-01-48"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-02-01.png" alt="picgo-2025-02-01-02-02-01"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-11-14-20.png" alt="picgo-2025-02-01-11-14-20"></details><p><strong>例 2</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-02-46.png" alt="picgo-2025-02-01-02-02-46"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-11-15-56.png" alt="picgo-2025-02-01-11-15-56"></details><p><strong>例 3</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-03-05.png" alt="picgo-2025-02-01-02-03-05"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-11-20-38.png" alt="picgo-2025-02-01-11-20-38"></details><h3 id="_6-代词-选项" tabindex="-1">6“代词”选项 <a class="header-anchor" href="#_6-代词-选项" aria-label="Permalink to “6“代词”选项”">​</a></h3><p>特征：选项中含有代词。</p><p>解法：代词找名词——应确认选项中的代词到底指代空格前的哪个名词或哪件事情。</p><p><strong>例 1</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-03-46.png" alt="picgo-2025-02-01-02-03-46"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-03-58.png" alt="picgo-2025-02-01-02-03-58"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-11-25-06.png" alt="picgo-2025-02-01-11-25-06"></details><p><strong>例 2</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-04-58.png" alt="picgo-2025-02-01-02-04-58"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-11-26-52.png" alt="picgo-2025-02-01-11-26-52"></details><p><strong>例 3</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-05-17.png" alt="picgo-2025-02-01-02-05-17"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-11-28-38.png" alt="picgo-2025-02-01-11-28-38"></details><p><strong>例 4</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-05-32.png" alt="picgo-2025-02-01-02-05-32"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-05-41.png" alt="picgo-2025-02-01-02-05-41"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-11-36-23.png" alt="picgo-2025-02-01-11-36-23"></details><p><strong>例 5</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-06-06.png" alt="picgo-2025-02-01-02-06-06"><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>31 空后一句，提到“你的第一次接头采访”，和新记者有关。所以 31 空填 D。</p></div><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-13-07-08.png" alt="picgo-2025-02-01-13-07-08"></details><p><strong>例 6</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-06-28.png" alt="picgo-2025-02-01-02-06-28"><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><ol start="72"><li><p>A</p></li><li><p>E</p></li></ol><p>This is when…… 说的是这是一个……的时间。说明 this 指代的是前一句话提到的时间。</p></div><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-13-19-02.png" alt="picgo-2025-02-01-13-19-02"></details><p><strong>例 7</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-06-44.png" alt="picgo-2025-02-01-02-06-44"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-06-54.png" alt="picgo-2025-02-01-02-06-54"><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><ol start="36"><li>B</li><li>G</li></ol></div><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-13-22-58.png" alt="picgo-2025-02-01-13-22-58"></details><h3 id="_7-逻辑词-选项" tabindex="-1">7“逻辑词”选项 <a class="header-anchor" href="#_7-逻辑词-选项" aria-label="Permalink to “7“逻辑词”选项”">​</a></h3><p>特征：所谓逻辑选项，就是含有“能够用来大致预测上下文所含内容的单词”的选项。</p><p>解法：一旦识破这类单词，直接预测空格上下文即可。</p><p><strong>例 1</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-07-57.png" alt="picgo-2025-02-01-02-07-57"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-08-08.png" alt="picgo-2025-02-01-02-08-08"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-13-44-00.png" alt="picgo-2025-02-01-13-44-00"></details><p><strong>例 2</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-08-25.png" alt="picgo-2025-02-01-02-08-25"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-13-44-33.png" alt="picgo-2025-02-01-13-44-33"></details><p><strong>例 3</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-08-39.png" alt="picgo-2025-02-01-02-08-39"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-08-49.png" alt="picgo-2025-02-01-02-08-49"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-13-46-48.png" alt="picgo-2025-02-01-13-46-48"></details><p><strong>例 4</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-09-15.png" alt="picgo-2025-02-01-02-09-15"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-09-26.png" alt="picgo-2025-02-01-02-09-26"><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><ol start="73"><li>A</li><li>G</li></ol></div><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-14-00-16.png" alt="picgo-2025-02-01-14-00-16"></details><p><strong>例 5</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-09-48.png" alt="picgo-2025-02-01-02-09-48"><p><s>A. That kept cities very small.</s></p><p><s>B. The rest live in small towns.</s></p><p>C. The effects of urban living on people should be considered.</p><p><s>D. Soon many other industrial nations become urban societies.</s></p><p><s>E. But even 200 years ago, only a few people could live in cities.</s></p><p><s>F. Modernization drew people to the cities and made farmers more productive.</s></p><p>G. Modern cities have destroyed social relations and the health of human beings.</p><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><ol start="71"><li>E</li><li>D</li><li>B</li><li>A</li><li>F</li></ol></div><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-16-22-30.png" alt="picgo-2025-02-01-16-22-30"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-16-22-38.png" alt="picgo-2025-02-01-16-22-38"></details><h3 id="_8-总-—-分-选项" tabindex="-1">8“总 — 分”选项 <a class="header-anchor" href="#_8-总-—-分-选项" aria-label="Permalink to “8“总 — 分”选项”">​</a></h3><p>特征：选项很多，一看就是下文各段的总述句，这样的选项主要表达这样的意思。</p><ul><li>① “接下来，教你几个方法”;</li><li>② “这些方法可以帮到你”;</li><li>③ “你应该怎么做呢？”;</li><li>④ “你 需 要 这 些 知 识” ;</li><li>⑤ &quot;有如下几个建议&quot;……</li></ul><p>解法：这样的选项，一般要放在<span class="text-red">第一段的末尾</span>，用来引领下文。</p><p><strong>例 1</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-11-16.png" alt="picgo-2025-02-01-02-11-16"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-18-45-02.png" alt="picgo-2025-02-01-18-45-02"></details><p><strong>例 2</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-11-34.png" alt="picgo-2025-02-01-02-11-34"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-11-44.png" alt="picgo-2025-02-01-02-11-44"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-18-52-26.png" alt="picgo-2025-02-01-18-52-26"></details><p><strong>例 3</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-12-04.png" alt="picgo-2025-02-01-02-12-04"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-18-59-18.png" alt="picgo-2025-02-01-18-59-18"></details><p><strong>例 4</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-12-21.png" alt="picgo-2025-02-01-02-12-21"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-19-00-11.png" alt="picgo-2025-02-01-19-00-11"></details><h2 id="_2-话题一致" tabindex="-1">2 话题一致 <a class="header-anchor" href="#_2-话题一致" aria-label="Permalink to “2 话题一致”">​</a></h2><p>经过上面大量例题的解读，我们发现，即便选项具备一些特征，我们依然要用“话题一致”这 个原则来对每道题做验证，才能保证每道题 100% 做对。所以，“话题一致”原则是阅读七选五当 之无愧的核心方法！</p><p>所谓话题一致，就是<span class="text-red">空格处应跟其紧紧相连的上下两句“谈论同样的事情”</span>。接下来，我们再 看几道例题，对“话题一致”进行更深刻的体会。</p><p><strong>例 1</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-13-38.png" alt="picgo-2025-02-01-02-13-38"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-13-46.png" alt="picgo-2025-02-01-02-13-46"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-19-01-56.png" alt="picgo-2025-02-01-19-01-56"></details><p><strong>例 2</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-13-59.png" alt="picgo-2025-02-01-02-13-59"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-19-06-15.png" alt="picgo-2025-02-01-19-06-15"></details><p><strong>例 3</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-14-11.png" alt="picgo-2025-02-01-02-14-11"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-14-22.png" alt="picgo-2025-02-01-02-14-22"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-19-09-34.png" alt="picgo-2025-02-01-19-09-34"></details><p><strong>例 4</strong></p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-02-14-47.png" alt="picgo-2025-02-01-02-14-47"><details class="details custom-block"><summary>Details</summary><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-19-31-01.png" alt="picgo-2025-02-01-19-31-01"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/picgo/picgo-2025-02-01-19-31-08.png" alt="picgo-2025-02-01-19-31-08"></details>`,176)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};