import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"companionship","description":"","frontmatter":{},"headers":[{"level":2,"title":"例句及解释","slug":"例句及解释","link":"#例句及解释","children":[]},{"level":2,"title":"与其他类似词汇的区别","slug":"与其他类似词汇的区别","link":"#与其他类似词汇的区别","children":[]}],"relativePath":"gaozhong/03_英语/02_自己总结/单词语法辨析/companionship.md","filePath":"gaozhong/03_英语/02_自己总结/单词语法辨析/companionship.md","lastUpdated":1741350421000}`);var o={name:`gaozhong/03_英语/02_自己总结/单词语法辨析/companionship.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="companionship" tabindex="-1">companionship <a class="header-anchor" href="#companionship" aria-label="Permalink to “companionship”">​</a></h1><p>“companionship”，这个单词是名词，意思是“友谊；陪伴；交谊”。</p><h2 id="例句及解释" tabindex="-1">例句及解释 <a class="header-anchor" href="#例句及解释" aria-label="Permalink to “例句及解释”">​</a></h2><p>The old man enjoys the companionship of his dog.（这位老人享受着他的狗的陪伴。）</p><p>在这个句子中，“companionship”体现了老人和狗之间相互陪伴的关系，这种陪伴给他带来愉悦的感觉。</p><p>Their companionship grew stronger over the years.（他们的友谊多年来变得更加深厚。）</p><p>这里“companionship”强调的是人与人之间的情谊随着时间而加深，更侧重于感情方面的联系。</p><h2 id="与其他类似词汇的区别" tabindex="-1">与其他类似词汇的区别 <a class="header-anchor" href="#与其他类似词汇的区别" aria-label="Permalink to “与其他类似词汇的区别”">​</a></h2><p>与“friendship”相比，“companionship”更强调陪伴的行为和这种陪伴所带来的情感联系。“friendship”主要侧重于朋友之间的情谊，不一定有陪伴这个行为要素。例如，你可能和远方的朋友有着深厚的“friendship”，但当下没有“companionship”。</p><p>与“company”（有“陪伴；同伴”的意思）相比，“companionship”是一种抽象的、情感化的概念。“company”可以具体指陪伴的人或事物，而“companionship”是从这种陪伴中衍生出来的情感和关系。例如，“I enjoy his company.”（我喜欢他的陪伴。）和“Our companionship is very precious to me.”（我们之间的情谊对我来说非常珍贵。）这两句话中，第一句侧重于享受他这个人的陪伴，第二句则强调这种陪伴所形成的珍贵情谊。</p>`,10)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};