import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"have no reservations about","description":"","frontmatter":{},"headers":[],"relativePath":"gaozhong/03_英语/02_自己总结/单词语法辨析/have no reservations about.md","filePath":"gaozhong/03_英语/02_自己总结/单词语法辨析/have no reservations about.md","lastUpdated":1741350421000}`);var o={name:`gaozhong/03_英语/02_自己总结/单词语法辨析/have no reservations about.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="have-no-reservations-about" tabindex="-1">have no reservations about <a class="header-anchor" href="#have-no-reservations-about" aria-label="Permalink to “have no reservations about”">​</a></h1><p>“have no reservations about”是一个常用的短语，意思是“对…… 毫无保留；对…… 毫不犹豫”。(= completely)</p><p><strong>一、例句及解释</strong></p><p>She has no reservations about expressing her opinions.</p><p>（她在表达自己的观点时毫无保留。）</p><p>在这里，“has no reservations about”表示她在表达观点这个行为上是非常直接、完全没有任何隐藏或者犹豫的。</p><p>The company has no reservations about investing in new technologies.</p><p>（公司对投资新技术毫不犹豫。）</p><p>说明公司在面对投资新技术这个决策时，是完全支持的态度，没有任何疑虑或者保留意见。</p><p><strong>二、语法结构</strong></p><p>这个短语中的“reservations”是名词，意思是“保留意见；疑虑”。</p><p>“have”是一个实义动词，根据主语的单复数和时态会有相应的变化，比如“has”（一般现在时，主语是第三人称单数），“had”（一般过去时）。“about”是一个介词，后面接名词、代词或者动名词，构成介词短语，用来表示“关于”的对象，即没有保留意见或者毫不犹豫的具体事情。</p><p>例如：</p><p>He has no reservations about helping others.（他对帮助别人毫不犹豫。）其中“helping others”是动名词短语，作为“about”的宾语。</p>`,14)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};