import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"relation 和 relationship 区别","description":"","frontmatter":{},"headers":[{"level":2,"title":"一、语义侧重","slug":"一、语义侧重","link":"#一、语义侧重","children":[]},{"level":2,"title":"二、语法和搭配","slug":"二、语法和搭配","link":"#二、语法和搭配","children":[]}],"relativePath":"gaozhong/03_英语/02_自己总结/单词语法辨析/relation 和 relationship 区别.md","filePath":"gaozhong/03_英语/02_自己总结/单词语法辨析/relation 和 relationship 区别.md","lastUpdated":1741350421000}`);var o={name:`gaozhong/03_英语/02_自己总结/单词语法辨析/relation 和 relationship 区别.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="relation-和-relationship-区别" tabindex="-1">relation 和 relationship 区别 <a class="header-anchor" href="#relation-和-relationship-区别" aria-label="Permalink to “relation 和 relationship 区别”">​</a></h1><p>“relation”和“relationship”这两个词在英语中都与“关系”有关，但它们在使用上有一些细微的差别：</p><h2 id="一、语义侧重" tabindex="-1">一、语义侧重 <a class="header-anchor" href="#一、语义侧重" aria-label="Permalink to “一、语义侧重”">​</a></h2><ol><li>Relation</li></ol><p>更侧重于强调事物之间的联系，这种联系可以是比较抽象的逻辑关联、亲属关系或者国家、组织之间的关联等。例如：</p><p>The relation between cause and effect is often complex.（因果之间的联系常常是复杂的。）这里“relation”用于描述因果这两个抽象概念之间的逻辑联系。</p><p>She is a distant relation of mine.（她是我的一个远房亲戚。）在这种亲属关系的表达中，“relation”强调的是亲属这一身份联系。</p><ol start="2"><li>Relationship</li></ol><p>更强调人与人之间、人与事物之间情感上的关联和相互依存的状态。例如：</p><p>Their relationship is based on mutual trust and respect.（他们的关系是建立在相互信任和尊重的基础上的。）此句中“relationship”重点在于描述两人之间情感层面的相互关系。</p><p>My relationship with my pet dog is very special.（我和我的宠物狗之间的关系很特别。）这里体现了人与宠物之间存在的一种特殊情感关联。</p><h2 id="二、语法和搭配" tabindex="-1">二、语法和搭配 <a class="header-anchor" href="#二、语法和搭配" aria-label="Permalink to “二、语法和搭配”">​</a></h2><ol><li>Relation</li></ol><p>常用的搭配有“in relation to”（关于；涉及），“bear no relation to”（与…… 毫无关系）。例如：</p><p>In relation to your request, we will give you a reply as soon as possible.（关于你的请求，我们会尽快给你答复。）</p><p>His story bears no relation to the facts.（他的说法与事实毫无关系。）</p><ol start="2"><li>Relationship</li></ol><p>可以用于“have a relationship with”（与…… 有关系）这样的结构。例如：</p><p>He has a close relationship with his colleagues.（他和他的同事们关系密切。）</p>`,19)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};