import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"the night before","description":"","frontmatter":{},"headers":[{"level":2,"title":"昨天（= 前一天）","slug":"昨天-前一天","link":"#昨天-前一天","children":[]},{"level":2,"title":"中文的前天","slug":"中文的前天","link":"#中文的前天","children":[]},{"level":2,"title":"英语怎么说前天","slug":"英语怎么说前天","link":"#英语怎么说前天","children":[]}],"relativePath":"gaozhong/03_英语/02_自己总结/单词语法辨析/the night before.md","filePath":"gaozhong/03_英语/02_自己总结/单词语法辨析/the night before.md","lastUpdated":1741350421000}`);var o={name:`gaozhong/03_英语/02_自己总结/单词语法辨析/the night before.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="the-night-before" tabindex="-1">the night before <a class="header-anchor" href="#the-night-before" aria-label="Permalink to “the night before”">​</a></h1><h2 id="昨天-前一天" tabindex="-1">昨天（= 前一天） <a class="header-anchor" href="#昨天-前一天" aria-label="Permalink to “昨天（= 前一天）”">​</a></h2><p>“the night before”常见的意思为 <strong>“前一天晚上” ，通常指在某个特定时间（如事件、日期等）之前的那个晚上</strong>。例如：</p><ul><li>I studied very hard the night before the exam.（考试前一天晚上我学习非常努力。）</li><li>She had a strange dream the night before her trip.（她在旅行前一天晚上做了个奇怪的梦。）</li></ul><p><strong>the night before 是指昨天的晚上吗？</strong></p><p>“the night before”不一定特指昨天晚上，它是相对于某个特定时间点而言的前一天晚上。具体解释如下：</p><ul><li>如果当前语境中的特定时间是今天，那么“the night before”指的就是昨天晚上。例如：I had a party last night. The night before, I was busy preparing for it.（我昨晚举办了一个派对。前一天晚上，我忙着做准备。）这里的“the night before”就是指昨天晚上。</li><li>但如果特定时间不是今天，那么“the night before”就不是昨天晚上。比如：The meeting was on Monday. The night before, he couldn&#39;t sleep well.（会议在周一举行。前一天晚上，他睡不好。）这里的“the night before”指的是周日晚上，而不是昨天晚上。</li></ul><h2 id="中文的前天" tabindex="-1">中文的前天 <a class="header-anchor" href="#中文的前天" aria-label="Permalink to “中文的前天”">​</a></h2><p>中文的“前天”指的是昨天的昨天。例如，今天是 2 月 1 日，那么昨天是 1 月 31 日，前天就是 1 月 30 日。</p><p><strong>但是</strong>，这里的英语说的是“前<strong>一</strong>天”</p><h2 id="英语怎么说前天" tabindex="-1">英语怎么说前天 <a class="header-anchor" href="#英语怎么说前天" aria-label="Permalink to “英语怎么说前天”">​</a></h2><p>“前天”常见的英语表达有“the day before yesterday”或者“two days ago”，以下对二者进行具体说明：</p><ul><li><strong>the day before yesterday</strong>：这种表达较为常用和直接，非常明确地表示“昨天的前一天”，即“前天”。例如：I met him the day before yesterday.（我前天遇见了他。）</li><li><strong>two days ago</strong>：从时间顺序角度来看，意思是“两天前”，在实际使用中也常用来表示“前天”。例如：We finished the work two days ago.（我们前天完成了工作。）</li></ul>`,13)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};