import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"分清关于考试的单词 Exam, Examination, Test, quiz","description":"","frontmatter":{},"headers":[{"level":2,"title":"examination/exam","slug":"examination-exam","link":"#examination-exam","children":[]},{"level":2,"title":"test","slug":"test","link":"#test","children":[]},{"level":2,"title":"quiz","slug":"quiz","link":"#quiz","children":[]},{"level":2,"title":"其它考试","slug":"其它考试","link":"#其它考试","children":[{"level":3,"title":"Midterm 丨期中考试","slug":"midterm-丨期中考试","link":"#midterm-丨期中考试","children":[]},{"level":3,"title":"Final 丨期末考试","slug":"final-丨期末考试","link":"#final-丨期末考试","children":[]},{"level":3,"title":"Closed-book exam 丨闭卷考试","slug":"closed-book-exam-丨闭卷考试","link":"#closed-book-exam-丨闭卷考试","children":[]},{"level":3,"title":"Open-book exam 丨开卷考试","slug":"open-book-exam-丨开卷考试","link":"#open-book-exam-丨开卷考试","children":[]}]}],"relativePath":"gaozhong/03_英语/02_自己总结/单词语法辨析/分清关于考试的单词 Exam, Examination, Test, quiz.md","filePath":"gaozhong/03_英语/02_自己总结/单词语法辨析/分清关于考试的单词 Exam, Examination, Test, quiz.md","lastUpdated":1741350421000}`);var o={name:`gaozhong/03_英语/02_自己总结/单词语法辨析/分清关于考试的单词 Exam, Examination, Test, quiz.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="分清关于考试的单词-exam-examination-test-quiz" tabindex="-1">分清关于考试的单词 Exam, Examination, Test, quiz <a class="header-anchor" href="#分清关于考试的单词-exam-examination-test-quiz" aria-label="Permalink to “分清关于考试的单词 Exam, Examination, Test, quiz”">​</a></h1><p>＂高考＂怎么说？</p><p>NCEE: National College Entrance Examination</p><p>说到 Examination＂考试＂，其实我们在一生中要面临无数场＂考试＂，但并不是每一场考试都＂隆重＂到用 Examination 这个词。英文字大体上分为:exam, test, quiz 三种，那么究竟有何区别？</p><h2 id="examination-exam" tabindex="-1">examination/exam <a class="header-anchor" href="#examination-exam" aria-label="Permalink to “examination/exam”">​</a></h2><p>examination 通常指正式考试，如期终考试、入学考试等。</p><p>exam 则是 examination 的缩写式，常用于口语中。</p><p><strong>例句</strong></p><p>He did very well in the entrance examination.</p><p>他在入学考试中成绩很好。</p><h2 id="test" tabindex="-1">test <a class="header-anchor" href="#test" aria-label="Permalink to “test”">​</a></h2><p>test 通常指按规定的标准，在可控的条件下，对事物进行彻底地检验，所以 test 指小考或考查。</p><p><strong>例句</strong></p><p>I have an English test this afternoon.</p><p>我今天下午有一个英语考试。</p><h2 id="quiz" tabindex="-1">quiz <a class="header-anchor" href="#quiz" aria-label="Permalink to “quiz”">​</a></h2><p>quiz 通常指“测验”，特指事先无准备，随时进行的测验，也可指（广播节目中的）一般知识测验、问答比赛、猜谜等。</p><p><strong>例句</strong></p><p>Our teacher likes to give quizzes before class.</p><p>我们老师特别喜欢在课前进行小测验。</p><h2 id="其它考试" tabindex="-1">其它考试 <a class="header-anchor" href="#其它考试" aria-label="Permalink to “其它考试”">​</a></h2><p>除了上面提到的，学生时代还会遇到这几类考试~</p><h3 id="midterm-丨期中考试" tabindex="-1">Midterm 丨期中考试 <a class="header-anchor" href="#midterm-丨期中考试" aria-label="Permalink to “Midterm 丨期中考试”">​</a></h3><p>Do you know what&#39;s going to be covered on the midterm?</p><p>你知道其中考试的范围是什么吗？</p><h3 id="final-丨期末考试" tabindex="-1">Final 丨期末考试 <a class="header-anchor" href="#final-丨期末考试" aria-label="Permalink to “Final 丨期末考试”">​</a></h3><p>The final will be a comprehensive test.</p><p>期末考是没有范围的。</p><h3 id="closed-book-exam-丨闭卷考试" tabindex="-1">Closed-book exam 丨闭卷考试 <a class="header-anchor" href="#closed-book-exam-丨闭卷考试" aria-label="Permalink to “Closed-book exam 丨闭卷考试”">​</a></h3><p>You definitely can&#39;t bring your book, it&#39;s a closed-book exam.</p><p>你当然不能带书了啊，这是闭卷考试。</p><h3 id="open-book-exam-丨开卷考试" tabindex="-1">Open-book exam 丨开卷考试 <a class="header-anchor" href="#open-book-exam-丨开卷考试" aria-label="Permalink to “Open-book exam 丨开卷考试”">​</a></h3><p>How I wish it could be an open-book exam!</p><p>我多希望能是开卷考试啊！</p>`,34)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};