import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"英语中的时间表达法","description":"","frontmatter":{},"headers":[{"level":2,"title":"一。时间点的表达","slug":"一。时间点的表达","link":"#一。时间点的表达","children":[{"level":3,"title":"1、所有的时间都可以用“小时 + 分钟”直接读：","slug":"_1、所有的时间都可以用-小时-分钟-直接读","link":"#_1、所有的时间都可以用-小时-分钟-直接读","children":[]},{"level":3,"title":"2、如果所表述的时间在半小时之内，可以用“分钟 + past + 小时”：","slug":"_2、如果所表述的时间在半小时之内-可以用-分钟-past-小时","link":"#_2、如果所表述的时间在半小时之内-可以用-分钟-past-小时","children":[]},{"level":3,"title":"3、如果所表述的时间在半小时之外，可以用“（相差的）分钟 + to + （下一）小时”：","slug":"_3、如果所表述的时间在半小时之外-可以用-相差的-分钟-to-下一-小时","link":"#_3、如果所表述的时间在半小时之外-可以用-相差的-分钟-to-下一-小时","children":[]},{"level":3,"title":"4、如果所表述的时间恰好为半小时，可以用“half + past + 小时”：","slug":"_4、如果所表述的时间恰好为半小时-可以用-half-past-小时","link":"#_4、如果所表述的时间恰好为半小时-可以用-half-past-小时","children":[]},{"level":3,"title":"5、如果所表述的分钟和 15 有关，就有三种表达法：","slug":"_5、如果所表述的分钟和-15-有关-就有三种表达法","link":"#_5、如果所表述的分钟和-15-有关-就有三种表达法","children":[]},{"level":3,"title":"6、整点的表达：","slug":"_6、整点的表达","link":"#_6、整点的表达","children":[]},{"level":3,"title":"7、大约时间：","slug":"_7、大约时间","link":"#_7、大约时间","children":[]},{"level":3,"title":"8、若想表明是上午，可在时间后加上 a.m.","slug":"_8、若想表明是上午-可在时间后加上-a-m","link":"#_8、若想表明是上午-可在时间后加上-a-m","children":[]},{"level":3,"title":"9.句子范例","slug":"_9-句子范例","link":"#_9-句子范例","children":[]}]},{"level":2,"title":"二。世纪、年代、年、月、日的表达","slug":"二。世纪、年代、年、月、日的表达","link":"#二。世纪、年代、年、月、日的表达","children":[{"level":3,"title":"1、世纪：","slug":"_1、世纪","link":"#_1、世纪","children":[]},{"level":3,"title":"2、年代","slug":"_2、年代","link":"#_2、年代","children":[]},{"level":3,"title":"3、年月日","slug":"_3、年月日","link":"#_3、年月日","children":[]},{"level":3,"title":"4. 介词的使用：","slug":"_4-介词的使用","link":"#_4-介词的使用","children":[]}]}],"relativePath":"gaozhong/03_英语/02_自己总结/单词语法辨析/英语中的时间表达法.md","filePath":"gaozhong/03_英语/02_自己总结/单词语法辨析/英语中的时间表达法.md","lastUpdated":1741350421000}`);var o={name:`gaozhong/03_英语/02_自己总结/单词语法辨析/英语中的时间表达法.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="英语中的时间表达法" tabindex="-1">英语中的时间表达法 <a class="header-anchor" href="#英语中的时间表达法" aria-label="Permalink to “英语中的时间表达法”">​</a></h1><h2 id="一。时间点的表达" tabindex="-1">一。时间点的表达 <a class="header-anchor" href="#一。时间点的表达" aria-label="Permalink to “一。时间点的表达”">​</a></h2><h3 id="_1、所有的时间都可以用-小时-分钟-直接读" tabindex="-1"><strong>1、所有的时间都可以用“小时 + 分钟”直接读：</strong> <a class="header-anchor" href="#_1、所有的时间都可以用-小时-分钟-直接读" aria-label="Permalink to “1、所有的时间都可以用“小时 + 分钟”直接读：”">​</a></h3><p>6:10 six ten</p><p>8:30 eight thirty</p><p>2:40 two forty</p><h3 id="_2、如果所表述的时间在半小时之内-可以用-分钟-past-小时" tabindex="-1"><strong>2、如果所表述的时间在半小时之内，可以用“分钟 + past + 小时”：</strong> <a class="header-anchor" href="#_2、如果所表述的时间在半小时之内-可以用-分钟-past-小时" aria-label="Permalink to “2、如果所表述的时间在半小时之内，可以用“分钟 + past + 小时”：”">​</a></h3><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>past 可以翻译为“过去”的意思。</p></div><p>6:10 ten past six</p><p>4:20 twenty past four</p><p>10:25 twenty-five past ten</p><h3 id="_3、如果所表述的时间在半小时之外-可以用-相差的-分钟-to-下一-小时" tabindex="-1"><strong>3、如果所表述的时间在半小时之外，可以用“（相差的）分钟 + to + （下一）小时”：</strong> <a class="header-anchor" href="#_3、如果所表述的时间在半小时之外-可以用-相差的-分钟-to-下一-小时" aria-label="Permalink to “3、如果所表述的时间在半小时之外，可以用“（相差的）分钟 + to + （下一）小时”：”">​</a></h3><p>10:35 twenty-five to eleven</p><p>5:50 ten to six</p><p>9:49 eleven to ten</p><h3 id="_4、如果所表述的时间恰好为半小时-可以用-half-past-小时" tabindex="-1"><strong>4、如果所表述的时间恰好为半小时，可以用“half + past + 小时”：</strong> <a class="header-anchor" href="#_4、如果所表述的时间恰好为半小时-可以用-half-past-小时" aria-label="Permalink to “4、如果所表述的时间恰好为半小时，可以用“half + past + 小时”：”">​</a></h3><p>11:30 half past eleven</p><p>2:30 half past two</p><h3 id="_5、如果所表述的分钟和-15-有关-就有三种表达法" tabindex="-1"><strong>5、如果所表述的分钟和 15 有关，就有三种表达法：</strong> <a class="header-anchor" href="#_5、如果所表述的分钟和-15-有关-就有三种表达法" aria-label="Permalink to “5、如果所表述的分钟和 15 有关，就有三种表达法：”">​</a></h3><p>（15 分钟又叫一刻钟：a quarter）</p><p>9:15 nine fifteen ; fifteen past nine ; a quarter past nine</p><p>3:45 three forty-five ; fifteen to four ; a quarter to four</p><h3 id="_6、整点的表达" tabindex="-1"><strong>6、整点的表达：</strong> <a class="header-anchor" href="#_6、整点的表达" aria-label="Permalink to “6、整点的表达：”">​</a></h3><p>现在是两点整。It&#39;s two./It&#39;s two o&#39;clock.</p><p>另外英语中的 noon 和 midnight 可分别直接表示白天和夜晚的 12 点：</p><p>It&#39;s (twelve) noon. 现在是中午十二点。</p><p>It&#39;s (twelve) midnight. 现在是半夜零点。</p><h3 id="_7、大约时间" tabindex="-1"><strong>7、大约时间：</strong> <a class="header-anchor" href="#_7、大约时间" aria-label="Permalink to “7、大约时间：”">​</a></h3><p>It&#39;s almost two. 马上到两点了。</p><p>It&#39;s not quite two. 还不到两点。</p><p>It&#39;s just after two. 刚过两点。</p><h3 id="_8、若想表明是上午-可在时间后加上-a-m" tabindex="-1"><strong>8、若想表明是上午，可在时间后加上 a.m.</strong> <a class="header-anchor" href="#_8、若想表明是上午-可在时间后加上-a-m" aria-label="Permalink to “8、若想表明是上午，可在时间后加上 a.m.”">​</a></h3><p>如：thirteen past six a.m.（上午六点十三分）。</p><p>若想表明是下午，可在时间后加上 p.m.</p><p>如：four o&#39;clock p.m.（下午四点）。</p><h3 id="_9-句子范例" tabindex="-1"><strong>9.句子范例</strong> <a class="header-anchor" href="#_9-句子范例" aria-label="Permalink to “9.句子范例”">​</a></h3><ol><li>It&#39;s nine forty-five. =It&#39;s a quarter to ten.</li><li>It&#39;s two seventeen. =It&#39;s seventeen past two.</li><li>It&#39;s three. =It&#39;s three o&#39;clock.</li><li>It&#39;s nine thirty. =It&#39;s half past nine.</li><li>It&#39;s six fifteen. =It&#39;s a quarter past six.</li><li>It&#39;s three fifty. =It&#39;s ten to four.</li></ol><h2 id="二。世纪、年代、年、月、日的表达" tabindex="-1">二。世纪、年代、年、月、日的表达 <a class="header-anchor" href="#二。世纪、年代、年、月、日的表达" aria-label="Permalink to “二。世纪、年代、年、月、日的表达”">​</a></h2><h3 id="_1、世纪" tabindex="-1">1、世纪： <a class="header-anchor" href="#_1、世纪" aria-label="Permalink to “1、世纪：”">​</a></h3><p><strong>①用“定冠词 + 序数词+century”表示</strong></p><p>例：在十七世纪 写作：in the 17th century，读作：in the seventeenth century</p><p><strong>②用“定冠词 + 百位进数+s”表示</strong></p><p>例：在十七世纪 写作：in the 1600s，读作：in the sixteen hundreds</p><p>注意：这种情况下，实际表达的世纪数是阿拉伯数字本身加一。</p><h3 id="_2、年代" tabindex="-1">2、年代 <a class="header-anchor" href="#_2、年代" aria-label="Permalink to “2、年代”">​</a></h3><p><strong>用“定冠词+(世纪百位进数 + 十位年代数)+s”表示</strong></p><p>例：在二十世纪三十年代 写作：in the 1930s，读作：in the thirties of the twentieth century 或 in the nineteen thirties</p><p><strong>表示某年代的早期、中期和晚期，可以在定冠词后添加 early, mid-和 late</strong></p><p>例如：</p><p>在二十世纪二十年代早期 in the early 1920s；</p><p>在二十世纪五十年代中期 in the mid-1950s</p><h3 id="_3、年月日" tabindex="-1">3、年月日 <a class="header-anchor" href="#_3、年月日" aria-label="Permalink to “3、年月日”">​</a></h3><h4 id="_1-年份" tabindex="-1">1）年份 <a class="header-anchor" href="#_1-年份" aria-label="Permalink to “1）年份”">​</a></h4><p><strong>① 读年份时一般分为两个单位来读，前两个数为一个，后两个数为一个：</strong></p><p>1949 读作：nineteen forty-nine 或 nineteen hundred and forty-nine</p><p><strong>② 如果是三位数，先读第一位，再把后两个数合起来读：</strong></p><p>253 读作：two fifty-three 或 two hundred and fifty-three</p><p><strong>③ 另外：2000 读作：two thousand，1902 读作：nineteen hundred and two 或 nineteen o two</strong></p><p><strong>④ 如果要使用 year，year 放在数词之前</strong></p><p>例如：in the year two fifty-three B.C. 在公元前 253 年</p><h4 id="_2-月份" tabindex="-1">2）月份 <a class="header-anchor" href="#_2-月份" aria-label="Permalink to “2）月份”">​</a></h4><p><strong>月份是专有名词，除了少数几个月份外都有缩写形式：</strong></p><p>January - Jan. 一月</p><p>February - Feb. 二月</p><p>March - Mar. 三月</p><p>April - Apr. 四月</p><p>August - Aug. 八月</p><p>September - Sept. 九月</p><p>October - Oct. 十月</p><p>November - Nov. 十一月</p><p>December - Dec. 十二月</p><p>注意：缩写形式后面的点不能省略，因为它是表示缩写形式的符号。</p><h4 id="_3-日期" tabindex="-1">3）日期： <a class="header-anchor" href="#_3-日期" aria-label="Permalink to “3）日期：”">​</a></h4><p>用序数词表示</p><p>例：十月一日 写作：October 1, October 1st, 1 October, 1st October, (the) 1st of October 等，其中的 October 都可以写成缩写形式 Oct.</p><p>读作：October the first 或 the first of October</p><h4 id="_4-年月日" tabindex="-1">4）年月日 <a class="header-anchor" href="#_4-年月日" aria-label="Permalink to “4）年月日”">​</a></h4><p><strong>用英语表达年月日的顺序：</strong></p><p><strong>① 月日年</strong></p><p>例：2002 年 1 月 17 日</p><p>写作：January 17(th), 2002 或 January seventeenth, 2002（日和年之间需用逗号隔开）</p><p>读作：January the seventeenth, two thousand and two</p><p><strong>②日月年</strong></p><p>例：2002 年 1 月 17 日</p><p>写作：17(th) January, 2002 或 the seventeenth of January, 2002（月和年之间需用逗号隔开）</p><p>读作：the seventeenth of January, two thousand and two</p><h3 id="_4-介词的使用" tabindex="-1">4. 介词的使用： <a class="header-anchor" href="#_4-介词的使用" aria-label="Permalink to “4. 介词的使用：”">​</a></h3><p>若指在哪一年或哪一月，用介词 in；若具体到某一天，需用介词 on。</p><p>例如：</p><p>She was born in 1989.</p><p>She was born in August.</p><p>She was born in August 1989.</p><p>She was born on 2nd August, 1989.</p>`,93)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};