import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"问题搜集","description":"","frontmatter":{},"headers":[{"level":3,"title":"1 英语前的<美>什么意思？","slug":"_1-英语前的-美-什么意思","link":"#_1-英语前的-美-什么意思","children":[]},{"level":3,"title":"2 词性的缩写","slug":"_2-词性的缩写","link":"#_2-词性的缩写","children":[]}],"relativePath":"gaozhong/03_英语/02_自己总结/问题搜集.md","filePath":"gaozhong/03_英语/02_自己总结/问题搜集.md","lastUpdated":1741350421000}`);var o={name:`gaozhong/03_英语/02_自己总结/问题搜集.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="问题搜集" tabindex="-1">问题搜集 <a class="header-anchor" href="#问题搜集" aria-label="Permalink to “问题搜集”">​</a></h1><h3 id="_1-英语前的-美-什么意思" tabindex="-1">1 英语前的&lt;美&gt;什么意思？ <a class="header-anchor" href="#_1-英语前的-美-什么意思" aria-label="Permalink to “1 英语前的&lt;美&gt;什么意思？”">​</a></h3><p>在英语单词或短语前面标注 &lt;美&gt;，是指这个单词或短语是美式英语（American English）的用法。</p><p>例如，“apartment &lt; 美&gt;”和“flat &lt; 英 &gt;”都表示“公寓”，这表明“apartment”是美国人常用的词汇来指代住宅建筑中的一个单元，而在英式英语中更习惯用“flat”来表达相同的意思。这种标注有助于区分英式英语和美式英语在词汇、拼写、发音或语法等方面的差异。</p><h3 id="_2-词性的缩写" tabindex="-1">2 词性的缩写 <a class="header-anchor" href="#_2-词性的缩写" aria-label="Permalink to “2 词性的缩写”">​</a></h3><p>prep = 介系词；前置词，preposition 的缩写</p><p>pron = 代名词，pronoun 的缩写</p><p>n = 名词，noun 的缩写</p><p>v = 动词，兼指及物动词和不及物动词，verb 的缩写</p><p>conj = 连接词，conjunction 的缩写</p><p>s = 主词</p><p>sc = 主词补语</p><p>o = 受词</p><p>oc = 受词补语</p><p>vi = 不及物动词，intransitive verb 的缩写（i 看作 in，表示 不，所以 vi 表示不及物动词）</p><p>vt = 及物动词，transitive verb 的缩写</p><p>aux.v = 助动词，auxiliary 的缩写</p><p>adj = 形容词，adjective 的缩写</p><p>ad = 副词，adverb 的缩写</p><p>art = 冠词，article 的缩写</p><p>num = 数词，numeral 的缩写</p><p>int = 感叹词，interjection 的缩写</p><p>u = 不可数名词，uncountable noun 的缩写</p><p>c = 可数名词，countable noun 的缩写</p><p>pl = 复数，plural 的缩写</p>`,25)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};