import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"04 研究有机化合物的一般方法","description":"","frontmatter":{},"headers":[{"level":2,"title":"有机化合物的分离、提纯","slug":"有机化合物的分离、提纯","link":"#有机化合物的分离、提纯","children":[{"level":3,"title":"蒸馏","slug":"蒸馏","link":"#蒸馏","children":[]},{"level":3,"title":"萃取","slug":"萃取","link":"#萃取","children":[]},{"level":3,"title":"重结晶","slug":"重结晶","link":"#重结晶","children":[]}]},{"level":2,"title":"重结晶法提纯苯甲酸","slug":"重结晶法提纯苯甲酸","link":"#重结晶法提纯苯甲酸","children":[]},{"level":2,"title":"有机化合物的实验式确定","slug":"有机化合物的实验式确定","link":"#有机化合物的实验式确定","children":[]},{"level":2,"title":"有机化合物的分子式确定","slug":"有机化合物的分子式确定","link":"#有机化合物的分子式确定","children":[{"level":3,"title":"红外线光谱","slug":"红外线光谱","link":"#红外线光谱","children":[]},{"level":3,"title":"核磁共振氢谱","slug":"核磁共振氢谱","link":"#核磁共振氢谱","children":[]},{"level":3,"title":"x 射线衍射","slug":"x-射线衍射","link":"#x-射线衍射","children":[]}]},{"level":2,"title":"研究有机化合物的一般方法总结","slug":"研究有机化合物的一般方法总结","link":"#研究有机化合物的一般方法总结","children":[]}],"relativePath":"gaozhong/05_化学/暂存/26 有机化学/04 研究有机化合物的一般方法.md","filePath":"gaozhong/05_化学/暂存/26 有机化学/04 研究有机化合物的一般方法.md","lastUpdated":1758138895000}`);var o={name:`gaozhong/05_化学/暂存/26 有机化学/04 研究有机化合物的一般方法.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="_04-研究有机化合物的一般方法" tabindex="-1">04 研究有机化合物的一般方法 <a class="header-anchor" href="#_04-研究有机化合物的一般方法" aria-label="Permalink to “04 研究有机化合物的一般方法”">​</a></h1><h2 id="有机化合物的分离、提纯" tabindex="-1">有机化合物的分离、提纯 <a class="header-anchor" href="#有机化合物的分离、提纯" aria-label="Permalink to “有机化合物的分离、提纯”">​</a></h2><h3 id="蒸馏" tabindex="-1">蒸馏 <a class="header-anchor" href="#蒸馏" aria-label="Permalink to “蒸馏”">​</a></h3><p>1．蒸馏原理：利用有机物与杂质的沸点差异，将有机化合物以蒸汽的形式蒸出，然后冷凝得到产品。</p><p>2．适用对象：互相溶解、沸点不同的液态有机混合物</p><p>3．适用条件：</p><p>①有机物的热稳定性较强；</p><p>②有机物与杂质的沸点相差较大（一般约大于 30 ℃）</p><p>4．实验装置与注意事项：</p><p>(1) 蒸馏烧瓶里盛液体的用量不超 2/3，不少于 1/3</p><p>(2) 加入沸石或碎瓷片，防止暴沸</p><p>(3) 温度计水银球应与蒸馏烧瓶的支管口平齐</p><p>(4) 冷凝水应下口进入，上口流出</p><p>(5) 实验开始时，先通冷凝水，后加热；实验结束时，先停止加热，后停止通冷凝水</p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/public/assets/allxueke/Snipaste_2024-10-08_15-47-10.png"><h3 id="萃取" tabindex="-1">萃取 <a class="header-anchor" href="#萃取" aria-label="Permalink to “萃取”">​</a></h3><ol><li>原理：</li></ol><p>(1) 液—液萃取：利用待分离组分在两种不互溶的溶剂中的溶解性不同，使待分离组分从溶解度较小的溶剂中转移到溶解度较大的溶剂中。</p><p>(2) 固—液萃取：用溶剂从固体物质中溶解出待分离组分</p><ol start="2"><li>萃取剂：</li></ol><p>(1) 选择原则：</p><p>①与原溶剂互不相溶；</p><p>②与溶质、原溶剂均不反应；</p><p>③溶质在萃取剂中的溶解度远大于原溶剂。</p><p>(2) 常用萃取剂：乙醚（C2H5OC2H5）、乙酸乙酯、二氯甲烷等</p><p>3．分液：将萃取后的两层液体（互不相溶、密度也不同的两种液体）分离开的操作方法。</p><p>4．主要仪器：分液漏斗</p><p>5．实验装置与注意事项：</p><p>操作步骤：检漏→加试剂振荡→静置分层→分液。</p><p>(1) 分液漏斗使用之前必须检漏</p><p>(2) 使用时需将漏斗上口的玻璃塞打开，或使玻璃塞上的凹槽对准分液漏斗上的小孔。</p><p>(3) 漏斗下端管口紧靠烧怀内壁，分液时下层液体从下口流出，上层液体从上口倒出。</p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/public/assets/allxueke/Snipaste_2024-10-08_15-48-57.png"><h3 id="重结晶" tabindex="-1">重结晶 <a class="header-anchor" href="#重结晶" aria-label="Permalink to “重结晶”">​</a></h3><p>1．重结晶原理：利用被提纯物质与杂质在同一溶剂中的溶解度不同而将杂质除去。</p><p>2．适用对象：固体有机化合物</p><p>3．溶剂选择：要求杂质在此溶剂中溶解度很小或溶解度很大，易于除去；被提纯的有机化合物在此溶剂中的溶解度受温度的影响较大，能够进行冷却结晶。</p><p>4．如果重结晶所得的晶体纯度不能达到要求，可以再次进行重结晶以提高产物的纯度。</p><h2 id="重结晶法提纯苯甲酸" tabindex="-1">重结晶法提纯苯甲酸 <a class="header-anchor" href="#重结晶法提纯苯甲酸" aria-label="Permalink to “重结晶法提纯苯甲酸”">​</a></h2><ol><li>实验目的：提纯含有少量氯化钠和泥沙杂质的苯甲酸</li></ol><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/public/assets/allxueke/Snipaste_2024-10-08_15-49-57.png"><h2 id="有机化合物的实验式确定" tabindex="-1">有机化合物的实验式确定 <a class="header-anchor" href="#有机化合物的实验式确定" aria-label="Permalink to “有机化合物的实验式确定”">​</a></h2><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/public/assets/allxueke/Snipaste_2024-10-08_15-50-29.png"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/public/assets/allxueke/Snipaste_2024-10-08_15-50-54.png"><h2 id="有机化合物的分子式确定" tabindex="-1">有机化合物的分子式确定 <a class="header-anchor" href="#有机化合物的分子式确定" aria-label="Permalink to “有机化合物的分子式确定”">​</a></h2><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/public/assets/allxueke/Snipaste_2024-10-08_15-51-31.png"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/public/assets/allxueke/Snipaste_2024-10-08_15-52-09.png"><h3 id="红外线光谱" tabindex="-1">红外线光谱 <a class="header-anchor" href="#红外线光谱" aria-label="Permalink to “红外线光谱”">​</a></h3><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/public/assets/allxueke/Snipaste_2024-10-08_15-52-42.png"><h3 id="核磁共振氢谱" tabindex="-1">核磁共振氢谱 <a class="header-anchor" href="#核磁共振氢谱" aria-label="Permalink to “核磁共振氢谱”">​</a></h3><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/public/assets/allxueke/Snipaste_2024-10-08_15-53-19.png"><h3 id="x-射线衍射" tabindex="-1">x 射线衍射 <a class="header-anchor" href="#x-射线衍射" aria-label="Permalink to “x 射线衍射”">​</a></h3><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/public/assets/allxueke/Snipaste_2024-10-08_15-53-49.png"><h2 id="研究有机化合物的一般方法总结" tabindex="-1">研究有机化合物的一般方法总结 <a class="header-anchor" href="#研究有机化合物的一般方法总结" aria-label="Permalink to “研究有机化合物的一般方法总结”">​</a></h2><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/public/assets/allxueke/Snipaste_2024-10-08_15-54-18.png">`,55)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};