import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"05 细胞核","description":"","frontmatter":{},"headers":[{"level":2,"title":"笔记","slug":"笔记","link":"#笔记","children":[]},{"level":2,"title":"课件","slug":"课件","link":"#课件","children":[]},{"level":2,"title":"其它","slug":"其它","link":"#其它","children":[]}],"relativePath":"gaozhong/06_生物/合集/一轮/02 细胞的基本结构/02 细胞结构与功能/05 细胞核.md","filePath":"gaozhong/06_生物/合集/一轮/02 细胞的基本结构/02 细胞结构与功能/05 细胞核.md","lastUpdated":1758138895000}`);var o={name:`gaozhong/06_生物/合集/一轮/02 细胞的基本结构/02 细胞结构与功能/05 细胞核.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="_05-细胞核" tabindex="-1">05 细胞核 <a class="header-anchor" href="#_05-细胞核" aria-label="Permalink to “05 细胞核”">​</a></h1><h2 id="笔记" tabindex="-1">笔记 <a class="header-anchor" href="#笔记" aria-label="Permalink to “笔记”">​</a></h2><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/xuexi/Snipaste_2024-12-20_00-43-15.png"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/xuexi/Snipaste_2024-12-20_00-43-22.png"><ul><li>细胞核能合成蛋白质吗？</li></ul><p>细胞核不能直接合成蛋白质，蛋白质的合成主要在细胞质中的核糖体上进行，但细胞核在蛋白质合成过程中起着至关重要的调控作用。</p><ul><li>核糖体的蛋白质来自哪里？</li></ul><p>核糖体中的蛋白质来源较为复杂，主要由细胞内的核糖体蛋白基因编码并在细胞质中合成。</p><p>核糖体蛋白质的合成：</p><p>前体 mRNA 在细胞核内经过一系列加工过程，然后，成熟的 mRNA 通过核孔复合物从细胞核转运到细胞质中。</p><p>在细胞质中，核糖体蛋白 mRNA 与核糖体结合，以 mRNA 为模板进行翻译，合成核糖体蛋白前体。</p><p>新合成的核糖体蛋白前体通常含有特定的靶向信号序列，这些信号序列引导它们被转运到细胞核内的核仁区域。核仁是核糖体组装的重要场所，在这里核糖体蛋白与 rRNA 进行组装。组装好的核糖体大小亚基从核仁输出到细胞质中，在细胞质中它们可以结合形成完整的核糖体，参与蛋白质的合成。</p><h2 id="课件" tabindex="-1">课件 <a class="header-anchor" href="#课件" aria-label="Permalink to “课件”">​</a></h2><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/xuexi/Snipaste_2024-12-21_06-50-13.png"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/xuexi/Snipaste_2024-12-21_06-50-50.png"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/xuexi/Snipaste_2024-12-21_06-51-17.png"><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/xuexi/Snipaste_2024-12-21_06-51-49.png"><h2 id="其它" tabindex="-1">其它 <a class="header-anchor" href="#其它" aria-label="Permalink to “其它”">​</a></h2><p>细胞核膜可以通过跨膜运输进出一些小分子，比如水。</p><p>核孔主要是用于进出大分子物质。</p><p>脱氧核苷酸可通过核膜。</p>`,21)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};