import{S as e,U as t,t as n,x as r}from"./chunks/vue.runtime.esm-bundler.CJTU9PXy.js";import{t as i}from"./chunks/plugin-vue_export-helper.BlZgCg_Z.js";n();const a=JSON.parse(`{"title":"细胞核的结构和功能","description":"","frontmatter":{},"headers":[{"level":2,"title":"细胞核的功能","slug":"细胞核的功能","link":"#细胞核的功能","children":[]},{"level":2,"title":"细胞核的结构","slug":"细胞核的结构","link":"#细胞核的结构","children":[]}],"relativePath":"gaozhong/06_生物/总结文章/细胞核的结构和功能.md","filePath":"gaozhong/06_生物/总结文章/细胞核的结构和功能.md","lastUpdated":1758138895000}`);var o={name:`gaozhong/06_生物/总结文章/细胞核的结构和功能.md`};function s(n,i,a,o,s,c){return t(),r(`div`,null,i[0]||=[e(`<h1 id="细胞核的结构和功能" tabindex="-1">细胞核的结构和功能 <a class="header-anchor" href="#细胞核的结构和功能" aria-label="Permalink to “细胞核的结构和功能”">​</a></h1><p>用光学显微镜观察细胞，最容易注意到的一个结构就是细胞核 (nucleus)。除了<strong>高等植物成熟的筛管细胞</strong>和<strong>哺乳动物成熟的红细胞</strong>等极少数细胞外，真核细胞都有细胞核。克隆牛的实例让我们看到了细胞核的重要性。细胞核究竟具有什么功能呢？</p><h2 id="细胞核的功能" tabindex="-1">细胞核的功能 <a class="header-anchor" href="#细胞核的功能" aria-label="Permalink to “细胞核的功能”">​</a></h2><p>大量的事实表明，<strong>细胞核控制着细胞的代谢和遗传因此</strong>，有人把细胞核比喻为细胞的“大脑”、细胞的“控制中心”。</p><h2 id="细胞核的结构" tabindex="-1">细胞核的结构 <a class="header-anchor" href="#细胞核的结构" aria-label="Permalink to “细胞核的结构”">​</a></h2><p>细胞核能够控制细胞的代谢和遗传，是与细胞核的结构分不开的。</p><img src="https://lukecheng2.oss-cn-chengdu.aliyuncs.com/xuexi/%E5%BE%AE%E4%BF%A1%E6%88%AA%E5%9B%BE_20241103154931.png"><p>细胞核中有 DNA。DNA 和蛋白质紧密结合成染色质 chromatin)。染色质是极细的丝状物，因容易被碱性染料染成深色而得名。细胞分裂时，细胞核解体，染色质高度螺旋化，缩短变粗，成为光学显微镜下清晰可见的圆柱状或杆状的染色体 (chromosome)。细胞分裂结束时，染色体解螺旋，重新成为细丝状的染色质，被包围在新形成的细胞核里。因此，染色质和染色体是同一物质在细胞不同时期的两种存在状态</p><p>DNA 上储存着遗传信息。在细胞分裂时，DNA 携带的遗传信息从亲代细胞传递给子代细胞，保证了亲子代细胞在遗传性状上的一致性。</p><p>因此，对细胞核功能较为全面的阐述应该是：<strong>细胞核是遗传信息库，是细胞代谢和遗传的控制中心。</strong></p>`,10)])}var c=i(o,[[`render`,s]]);export{a as __pageData,c as default};